def unsmoothed_ngram_log_prob(corpus: list[str], sequence: list[str], N: int) -> float:
    """Compute the unsmoothed MLE N-gram log-probability of *sequence*."""
    if len(sequence) < N:
        return round(0.0, 4)
    if N == 1:
        total_words = len(corpus)
        if total_words == 0:
            return float('-inf')
        word_counts = Counter(corpus)
        for word in sequence:
            if word_counts.get(word, 0) == 0:
                return float('-inf')
        log_prob = 0.0
        for word in sequence:
            prob = word_counts[word] / total_words
            log_prob += np.log(prob)
        return round(log_prob, 4)
    else:
        ngram_counts = Counter()
        for i in range(len(corpus) - N + 1):
            ngram = tuple(corpus[i:i + N])
            ngram_counts[ngram] += 1
        nminus1_counts = Counter()
        for i in range(len(corpus) - (N - 1) + 1):
            ngram = tuple(corpus[i:i + (N - 1)])
            nminus1_counts[ngram] += 1
        log_prob = 0.0
        for i in range(len(sequence) - N + 1):
            current_ngram = tuple(sequence[i:i + N])
            if ngram_counts.get(current_ngram, 0) == 0:
                return float('-inf')
            prefix = current_ngram[:-1]
            if nminus1_counts.get(prefix, 0) == 0:
                return float('-inf')
            numerator = ngram_counts[current_ngram]
            denominator = nminus1_counts[prefix]
            if denominator == 0:
                return float('-inf')
            prob = numerator / denominator
            log_prob += np.log(prob)
        return round(log_prob, 4)
