def fp_growth(transactions: list[list[str]], min_support: int) -> list[list[str]]:
    """Discover every frequent item-set in *transactions* with FP-Growth."""
    if not transactions:
        return []
    item_counts = Counter()
    for transaction in transactions:
        unique_items = set(transaction)
        for item in unique_items:
            item_counts[item] += 1
    frequent_items = {item for (item, count) in item_counts.items() if count >= min_support}
    if not frequent_items:
        return []
    sorted_items = sorted(frequent_items, key=lambda x: (-item_counts[x], x))
    root = {'name': None, 'count': 0, 'children': {}, 'parent': None}
    header_table = defaultdict(list)
    for transaction in transactions:
        transaction_items = [item for item in transaction if item in frequent_items]
        item_index = {item: idx for (idx, item) in enumerate(sorted_items)}
        transaction_items_sorted = sorted(transaction_items, key=lambda x: item_index[x])
        current_node = root
        for item in transaction_items_sorted:
            if item not in current_node['children']:
                new_node = {'name': item, 'count': 0, 'children': {}, 'parent': current_node}
                current_node['children'][item] = new_node
                header_table[item].append(new_node)
            current_node = current_node['children'][item]
            current_node['count'] += 1

    def mine(node, header, sorted_items, min_sup, current_prefix):
        result = []
        for item in reversed(sorted_items):
            nodes = header.get(item, [])
            support = sum((n['count'] for n in nodes))
            if support >= min_sup:
                conditional_transactions = []
                for node in nodes:
                    prefix = []
                    current = node['parent']
                    while current and current['name'] is not None:
                        prefix.insert(0, current['name'])
                        current = current['parent']
                    conditional_transactions.append(prefix)
                cond_root = {'name': None, 'count': 0, 'children': {}, 'parent': None}
                cond_header = defaultdict(list)
                for trans in conditional_transactions:
                    current_node = cond_root
                    for item_in_trans in trans:
                        if item_in_trans not in current_node['children']:
                            new_node = {'name': item_in_trans, 'count': 0, 'children': {}, 'parent': current_node}
                            current_node['children'][item_in_trans] = new_node
                            cond_header[item_in_trans].append(new_node)
                        current_node = current_node['children'][item_in_trans]
                        current_node['count'] += 1
                itemsets = mine(cond_root, cond_header, sorted_items, min_sup, current_prefix + [item])
                result.extend(itemsets)
        return result
    frequent_itemsets = mine(root, header_table, sorted_items, min_support, [])
    frequent_itemsets = [sorted(itemset, key=lambda x: x) for itemset in frequent_itemsets]
    frequent_itemsets.sort(key=lambda x: (len(x), x))
    return frequent_itemsets
