import numpy as np
def decision_tree_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, max_depth: int | None=None) -> list[int]:
    """Build a CART decision tree on (X_train, y_train) and predict labels for X_test."""

    def gini(y):
        if len(y) == 0:
            return 0
        p = np.bincount(y) / len(y)
        return 1 - np.sum(p ** 2)

    def find_best_split(X, y):
        best_gain = -1
        best_feature = 0
        best_threshold = 0
        n_samples = X.shape[0]
        if n_samples == 0:
            return (best_feature, best_threshold, best_gain)
        parent_gini = gini(y)
        for feature in range(X.shape[1]):
            sorted_indices = np.argsort(X[:, feature])
            X_sorted = X[sorted_indices, feature]
            y_sorted = y[sorted_indices]
            thresholds = []
            for i in range(len(X_sorted) - 1):
                if X_sorted[i] == X_sorted[i + 1]:
                    continue
                threshold = (X_sorted[i] + X_sorted[i + 1]) / 2
                thresholds.append(threshold)
            for threshold in thresholds:
                left_indices = X[:, feature] <= threshold
                right_indices = ~left_indices
                y_left = y[left_indices]
                y_right = y[right_indices]
                if len(y_left) == 0 or len(y_right) == 0:
                    continue
                gini_left = gini(y_left)
                gini_right = gini(y_right)
                weight_left = len(y_left) / n_samples
                weight_right = len(y_right) / n_samples
                children_gini = weight_left * gini_left + weight_right * gini_right
                gain = parent_gini - children_gini
                if gain > best_gain:
                    best_gain = gain
                    best_feature = feature
                    best_threshold = threshold
        return (best_feature, best_threshold, best_gain)

    def build_tree(X, y, current_depth):
        if len(np.unique(y)) == 1:
            return {'class': int(y[0])}
        if max_depth is not None and current_depth >= max_depth:
            majority_class = np.bincount(y).argmax()
            return {'class': int(majority_class)}
        (best_feature, best_threshold, best_gain) = find_best_split(X, y)
        if best_gain <= 0:
            majority_class = np.bincount(y).argmax()
            return {'class': int(majority_class)}
        left_indices = X[:, best_feature] <= best_threshold
        right_indices = ~left_indices
        X_left = X[left_indices]
        y_left = y[left_indices]
        X_right = X[right_indices]
        y_right = y[right_indices]
        if len(y_left) == 0 or len(y_right) == 0:
            majority_class = np.bincount(y).argmax()
            return {'class': int(majority_class)}
        left_child = build_tree(X_left, y_left, current_depth + 1)
        right_child = build_tree(X_right, y_right, current_depth + 1)
        return {'feature': best_feature, 'threshold': best_threshold, 'left': left_child, 'right': right_child}

    def predict_sample(x, tree):
        while 'class' not in tree:
            feature = tree['feature']
            threshold = tree['threshold']
            if x[feature] <= threshold:
                tree = tree['left']
            else:
                tree = tree['right']
        return tree['class']
    if X_train.shape[0] == 0:
        return []
    tree = build_tree(X_train, y_train, 0)
    predictions = []
    for x in X_test:
        predictions.append(predict_sample(x, tree))
    return predictions
