import math
from collections import defaultdict
import numpy as np
def maxent_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, epsilon: float=0.001, n_iter: int=100) -> list[int]:
    empirical_counts = defaultdict(int)
    for i in range(X_train.shape[0]):
        x = X_train[i]
        y = y_train[i]
        for j in range(x.shape[0]):
            v = x[j]
            feature = (j, v, y)
            empirical_counts[feature] += 1
    weights = {feature: 0.0 for feature in empirical_counts}
    possible_ys = np.unique(y_train)
    for _ in range(n_iter):
        expected_counts = defaultdict(float)
        for x in X_train:
            sum_weights = {}
            for y in possible_ys:
                s = 0.0
                for j in range(x.shape[0]):
                    v = x[j]
                    feature = (j, v, y)
                    if feature in weights:
                        s += weights[feature]
                sum_weights[y] = s
            Z = 0.0
            for y in possible_ys:
                Z += math.exp(sum_weights[y])
            for y in possible_ys:
                if Z == 0:
                    p = 0.0
                else:
                    p = math.exp(sum_weights[y]) / Z
                for j in range(x.shape[0]):
                    v = x[j]
                    feature = (j, v, y)
                    if feature in weights:
                        expected_counts[feature] += p
        max_delta = 0.0
        for feature in weights:
            emp = empirical_counts[feature]
            exp = expected_counts.get(feature, 0.0)
            if exp < 1e-10:
                exp = 1e-10
            delta = (emp - exp) / exp
            weights[feature] += delta
            if abs(delta) > max_delta:
                max_delta = abs(delta)
        if max_delta < epsilon:
            break
    predicted_labels = []
    for x_test in X_test:
        sum_weights = {}
        for y in possible_ys:
            s = 0.0
            for j in range(x_test.shape[0]):
                v = x_test[j]
                feature = (j, v, y)
                if feature in weights:
                    s += weights[feature]
            sum_weights[y] = s
        Z = sum((math.exp(s) for s in sum_weights.values()))
        if Z == 0:
            predicted_y = possible_ys[0]
        else:
            max_p = -1
            predicted_y = possible_ys[0]
            for y in possible_ys:
                p = math.exp(sum_weights[y]) / Z
                if p > max_p:
                    max_p = p
                    predicted_y = y
        predicted_labels.append(predicted_y)
    return predicted_labels
assert maxent_predict(np.array([[0],[1]]), np.array([0,1]), np.array([[1],[0]])) == [1,0], "Test-1 failed: basic two-sample training"
assert maxent_predict(np.array([[0],[0],[1],[1]]), np.array([0,0,1,1]), np.array([[1],[0]])) == [1,0], "Test-2 failed: duplicated training rows"
assert maxent_predict(np.array([[0,0],[1,0]]), np.array([0,1]), np.array([[0,0],[1,0],[0,0]])) == [0,1,0], "Test-3 failed: two features, two labels"
assert maxent_predict(np.array([[0],[1],[2]]), np.array([0,1,2]), np.array([[2],[0]])) == [2,0], "Test-4 failed: three labels"
assert maxent_predict(np.array([[0,0],[0,1],[1,0]]), np.array([0,1,2]), np.array([[0,1],[1,0]])) == [1,2], "Test-5 failed: 3-class, 2-feature"
assert maxent_predict(np.array([[0,1,0],[1,0,1]]), np.array([0,1]), np.array([[1,0,1]])) == [1], "Test-6 failed: 3 features"
assert maxent_predict(np.array([[0],[0],[0],[1]]), np.array([1,1,1,0]), np.array([[1],[0]])) == [0,1], "Test-7 failed: imbalanced classes"
assert maxent_predict(np.array([[0,0],[0,1],[0,2],[1,0],[1,1],[1,2]]), np.array([0,0,0,1,1,1]), np.array([[1,1],[0,2]])) == [1,0], "Test-8 failed: bigger balanced dataset"
assert maxent_predict(np.array([[5],[6],[7]]), np.array([2,2,2]), np.array([[5],[7]])) == [2,2], "Test-9 failed: single-class training"
assert maxent_predict(np.array([[0],[1]]), np.array([1,0]), np.array([[0],[1]])) == [1,0], "Test-10 failed: labels reversed order"
