from typing import List, Dict, Tuple, Hashable
def find_frequent_itemsets(transactions: List[List[Hashable]], minsup: float) -> Dict[Tuple[Hashable, ...], int]:
    """Return all frequent itemsets in **transactions**.

    Args:
        transactions: List where each element represents a transaction and is
                       itself a list of items (hashable Python objects).
        minsup:       Minimum support given as a fraction between 0 and 1.

    Returns:
        A dictionary that maps every frequent itemset (stored as a tuple of
        sorted items) to its absolute support count.  If no itemset reaches
        the threshold the function must return an empty dictionary.
    """
    if not transactions:
        return {}
    N = len(transactions)
    threshold = math.ceil(minsup * N)
    transaction_sets = [set(transaction) for transaction in transactions]
    unique_items = set()
    for transaction in transactions:
        unique_items.update(transaction)
    unique_items = sorted(list(unique_items))
    frequent_itemsets = {}
    for k in range(1, len(unique_items) + 1):
        for combo in combinations(unique_items, k):
            support = 0
            for t_set in transaction_sets:
                if all((item in t_set for item in combo)):
                    support += 1
            if support >= threshold:
                frequent_itemsets[combo] = support
    return frequent_itemsets

import numpy as np
def softplus(x, deriv: bool=False):
    """Implements the SoftPlus activation function and its derivative.

    Args:
        x: A scalar, list, or NumPy array of real numbers.
        deriv: A boolean flag indicating whether to compute the derivative.
               Defaults to False.

    Returns:
        The SoftPlus activation (if deriv=False) or its derivative (if deriv=True)
        for each element in x, rounded to the nearest 4th decimal. If x is a
        scalar, returns a float; otherwise, returns a list.
    """
    is_scalar = np.isscalar(x)
    x = np.array(x, dtype=np.float64)
    if deriv:
        result = np.where(x > 20, 1.0, np.where(x < -20, 0.0, 1 / (1 + np.exp(-x))))
    else:
        result = np.where(x > 20, x, np.where(x < -20, 0.0, np.log1p(np.exp(x))))
    rounded = np.round(result, 4)
    if is_scalar:
        return rounded.item()
    else:
        return rounded.tolist()

import numpy as np
from collections import Counter
def unsmoothed_ngram_log_prob(corpus: list[str], sequence: list[str], N: int) -> float:
    """Compute the unsmoothed MLE N-gram log-probability of *sequence*."""
    if len(sequence) < N:
        return round(0.0, 4)
    if N == 1:
        total_words = len(corpus)
        if total_words == 0:
            return float('-inf')
        word_counts = Counter(corpus)
        for word in sequence:
            if word_counts.get(word, 0) == 0:
                return float('-inf')
        log_prob = 0.0
        for word in sequence:
            prob = word_counts[word] / total_words
            log_prob += np.log(prob)
        return round(log_prob, 4)
    else:
        ngram_counts = Counter()
        for i in range(len(corpus) - N + 1):
            ngram = tuple(corpus[i:i + N])
            ngram_counts[ngram] += 1
        nminus1_counts = Counter()
        for i in range(len(corpus) - (N - 1) + 1):
            ngram = tuple(corpus[i:i + (N - 1)])
            nminus1_counts[ngram] += 1
        log_prob = 0.0
        for i in range(len(sequence) - N + 1):
            current_ngram = tuple(sequence[i:i + N])
            if ngram_counts.get(current_ngram, 0) == 0:
                return float('-inf')
            prefix = current_ngram[:-1]
            if nminus1_counts.get(prefix, 0) == 0:
                return float('-inf')
            numerator = ngram_counts[current_ngram]
            denominator = nminus1_counts[prefix]
            if denominator == 0:
                return float('-inf')
            prob = numerator / denominator
            log_prob += np.log(prob)
        return round(log_prob, 4)

def forward_algorithm(S: list[float], A: list[list[float]], B: list[list[float]], observations: list[int]) -> float:
    """Forward algorithm for Hidden Markov Models.

    Args:
        S (list[float]): Initial state probabilities.
        A (list[list[float]]): State–transition probabilities.
        B (list[list[float]]): Emission probabilities.
        observations (list[int]): Observation index sequence.

    Returns:
        float: Sequence likelihood rounded to 4 decimals, or −1 on invalid input.
    """
    n = len(S)
    if n == 0:
        return -1
    for row in A:
        if len(row) != n:
            return -1
    m = 0
    if B:
        m = len(B[0])
        for row in B:
            if len(row) != m:
                return -1
    if not observations:
        return -1
    for obs in observations:
        if not isinstance(obs, int) or obs < 0 or obs >= m:
            return -1
    current_alpha = [s for s in S]
    for obs in observations:
        next_alpha = [0.0] * n
        for i in range(n):
            sum_trans = 0.0
            for j in range(n):
                sum_trans += current_alpha[j] * A[j][i]
            emission_prob = B[i][obs]
            next_alpha[i] = sum_trans * emission_prob
        current_alpha = next_alpha
    result = sum(current_alpha)
    return round(result, 4)

from collections import Counter, defaultdict
def fp_growth(transactions: list[list[str]], min_support: int) -> list[list[str]]:
    """Discover every frequent item-set in *transactions* with FP-Growth."""
    if not transactions:
        return []
    item_counts = Counter()
    for transaction in transactions:
        unique_items = set(transaction)
        for item in unique_items:
            item_counts[item] += 1
    frequent_items = {item for (item, count) in item_counts.items() if count >= min_support}
    if not frequent_items:
        return []
    sorted_items = sorted(frequent_items, key=lambda x: (-item_counts[x], x))
    root = {'name': None, 'count': 0, 'children': {}, 'parent': None}
    header_table = defaultdict(list)
    for transaction in transactions:
        transaction_items = [item for item in transaction if item in frequent_items]
        item_index = {item: idx for (idx, item) in enumerate(sorted_items)}
        transaction_items_sorted = sorted(transaction_items, key=lambda x: item_index[x])
        current_node = root
        for item in transaction_items_sorted:
            if item not in current_node['children']:
                new_node = {'name': item, 'count': 0, 'children': {}, 'parent': current_node}
                current_node['children'][item] = new_node
                header_table[item].append(new_node)
            current_node = current_node['children'][item]
            current_node['count'] += 1

    def mine(node, header, sorted_items, min_sup, current_prefix):
        result = []
        for item in reversed(sorted_items):
            nodes = header.get(item, [])
            support = sum((n['count'] for n in nodes))
            if support >= min_sup:
                conditional_transactions = []
                for node in nodes:
                    prefix = []
                    current = node['parent']
                    while current and current['name'] is not None:
                        prefix.insert(0, current['name'])
                        current = current['parent']
                    conditional_transactions.append(prefix)
                cond_root = {'name': None, 'count': 0, 'children': {}, 'parent': None}
                cond_header = defaultdict(list)
                for trans in conditional_transactions:
                    current_node = cond_root
                    for item_in_trans in trans:
                        if item_in_trans not in current_node['children']:
                            new_node = {'name': item_in_trans, 'count': 0, 'children': {}, 'parent': current_node}
                            current_node['children'][item_in_trans] = new_node
                            cond_header[item_in_trans].append(new_node)
                        current_node = current_node['children'][item_in_trans]
                        current_node['count'] += 1
                itemsets = mine(cond_root, cond_header, sorted_items, min_sup, current_prefix + [item])
                result.extend(itemsets)
        return result
    frequent_itemsets = mine(root, header_table, sorted_items, min_support, [])
    frequent_itemsets = [sorted(itemset, key=lambda x: x) for itemset in frequent_itemsets]
    frequent_itemsets.sort(key=lambda x: (len(x), x))
    return frequent_itemsets

