from collections import defaultdict
def additive_ngram_log_prob(corpus: list[str], sequence: str, N: int, K: float=1.0) -> float:
    """Compute the additive-smoothed log-probability of *sequence* given a corpus."""
    ngram_counts = {n: defaultdict(int) for n in range(1, N + 1)}
    vocabulary = set()
    num_sentences = len(corpus)
    for sentence in corpus:
        words = sentence.split()
        for word in words:
            vocabulary.add(word)
        for n in range(1, N + 1):
            for i in range(len(words) - n + 1):
                ngram = tuple(words[i:i + n])
                ngram_counts[n][ngram] += 1
    vocabulary.add('<UNK>')
    vocabulary_size = len(vocabulary)
    sequence_words = sequence.split()
    for i in range(len(sequence_words)):
        if sequence_words[i] not in vocabulary:
            sequence_words[i] = '<UNK>'
    if len(sequence_words) < N:
        return 0.0
    total_log_prob = 0.0
    for i in range(len(sequence_words) - N + 1):
        context = sequence_words[i:i + N - 1]
        word = sequence_words[i + N - 1]
        ngram = tuple(context + [word])
        count_ngram = ngram_counts[N].get(ngram, 0)
        if N == 1:
            count_context = num_sentences
        else:
            context_tuple = tuple(context)
            count_context = ngram_counts[N - 1].get(context_tuple, 0)
        numerator = count_ngram + K
        denominator = count_context + K * vocabulary_size
        prob = numerator / denominator
        total_log_prob += math.log(prob)
    return round(total_log_prob, 4)

import numpy as np
TIME_STEPS = 20
def string_to_int(text: str, time_steps: int, vocabulary: dict[str, int]) -> list[int]:
    """Encodes a string into a list of integers using a vocabulary."""
    encoded = []
    for char in text:
        encoded.append(vocabulary.get(char, 0))
    if len(encoded) < time_steps:
        encoded += [0] * (time_steps - len(encoded))
    else:
        encoded = encoded[:time_steps]
    return encoded
def int_to_string(indices, inverse_vocab: dict[int, str]) -> str:
    """Converts a list of integer indices back to a string using an inverse vocabulary."""
    result = []
    for idx in indices:
        if idx != 0:
            result.append(inverse_vocab.get(idx, ''))
    return ''.join(result)
def run_example(model, input_vocabulary: dict[str, int], inv_output_vocabulary: dict[int, str], text: str) -> str:
    """Encodes the input text, runs it through the model, and decodes the output."""
    encoded_input = string_to_int(text, TIME_STEPS, input_vocabulary)
    encoded_input = np.array(encoded_input, dtype=np.int64)
    encoded_input = encoded_input.reshape(1, -1)
    predictions = model.predict(encoded_input)
    predicted_ids = np.argmax(predictions, axis=-1)
    decoded = int_to_string(predicted_ids[0], inv_output_vocabulary)
    return decoded

