import bisect
def best_gini_split(X, y):
    """Find the best feature index and threshold that minimise the weighted Gini impurity.

    Parameters
    ----------
    X : list[list[float]] or numpy.ndarray
        A 2-D structure where each inner list/row contains the numerical
        feature values of one sample.
    y : list[int] or numpy.ndarray
        A 1-D structure containing the class labels corresponding to the rows
        of X.

    Returns
    -------
    tuple
        A tuple `(best_feature_index, best_threshold_value, best_gini)` where
        `best_gini` is rounded to 4 decimal places.  If no valid split exists
        the function must return `(-1, None, round(G_whole_dataset, 4))`.
    """
    if not isinstance(X, np.ndarray):
        X = np.array(X)
    n_samples = len(y)
    if n_samples == 0:
        return (-1, None, 0.0)
    n_features = X.shape[1]
    unique_classes = np.unique(y)
    if len(unique_classes) == 1:
        return (-1, None, 0.0)
    counts = np.array([np.sum(y == c) for c in unique_classes])
    total = n_samples
    g_whole = 1.0 - np.sum((counts / total) ** 2)
    if g_whole == 0.0:
        return (-1, None, round(g_whole, 4))
    best_feature = None
    best_threshold = None
    best_gini = float('inf')
    for j in range(n_features):
        X_j = X[:, j]
        sorted_pairs = sorted(zip(X_j, y), key=lambda x: x[0])
        sorted_X_j = np.array([x[0] for x in sorted_pairs])
        sorted_y = np.array([x[1] for x in sorted_pairs])
        unique_v = np.unique(X_j)
        unique_v = np.sort(unique_v)
        prefix = {c: np.zeros(n_samples + 1, dtype=int) for c in unique_classes}
        for c in unique_classes:
            mask = (sorted_y == c).astype(int)
            prefix[c][1:] = np.cumsum(mask)
        for v in unique_v:
            i = bisect.bisect_right(sorted_X_j, v) - 1
            left_count = i + 1
            right_count = n_samples - left_count
            if right_count == 0:
                continue
            g_left = 1.0
            for c in unique_classes:
                cnt = prefix[c][left_count]
                if left_count == 0:
                    p = 0.0
                else:
                    p = cnt / left_count
                g_left -= p ** 2
            g_right = 1.0
            for c in unique_classes:
                cnt = prefix[c][n_samples] - prefix[c][left_count]
                if right_count == 0:
                    p = 0.0
                else:
                    p = cnt / right_count
                g_right -= p ** 2
            g_split = left_count / n_samples * g_left + right_count / n_samples * g_right
            if best_gini == float('inf'):
                best_feature = j
                best_threshold = v
                best_gini = g_split
            elif g_split < best_gini:
                best_feature = j
                best_threshold = v
                best_gini = g_split
            elif np.isclose(g_split, best_gini, atol=1e-12):
                if j < best_feature:
                    best_feature = j
                    best_threshold = v
                    best_gini = g_split
                elif j == best_feature:
                    if v < best_threshold:
                        best_threshold = v
                        best_gini = g_split
    if best_feature is None:
        return (-1, None, round(g_whole, 4))
    else:
        return (best_feature, best_threshold, round(best_gini, 4))

import numpy as np
def metric(name: str):
    """Factory producing a validated distance function."""
    supported_metrics = ['euclidean', 'manhattan', 'chebyshev', 'cosine']
    if name not in supported_metrics:
        return lambda x, y: -1

    def d(x, y):
        try:
            x = np.asarray(x, dtype=np.float64)
            y = np.asarray(y, dtype=np.float64)
        except:
            return -1
        if x.ndim != 1 or y.ndim != 1:
            return -1
        if x.shape[0] != y.shape[0] or x.shape[0] == 0:
            return -1
        if name == 'cosine':
            norm_x = np.linalg.norm(x)
            norm_y = np.linalg.norm(y)
            if norm_x < 1e-10 or norm_y < 1e-10:
                return -1
        if name == 'euclidean':
            distance = np.sqrt(np.sum((x - y) ** 2))
        elif name == 'manhattan':
            distance = np.sum(np.abs(x - y))
        elif name == 'chebyshev':
            distance = np.max(np.abs(x - y))
        elif name == 'cosine':
            dot_product = np.dot(x, y)
            distance = 1.0 - dot_product / (norm_x * norm_y)
        else:
            return -1
        return round(distance, 4)
    return d

def blackjack_outcome(player: list[int], dealer: list[int]) -> float:
    """Evaluate the outcome of a finished round of Blackjack.

    The function **must** follow the rules presented in the task description.

    Args:
        player: List of integers (1–10) representing the player's final hand. 1 is Ace.
        dealer: List of integers (1–10) representing the dealer's final hand.

    Returns:
        The player's reward as a float. Possible values are -1, 0, 1 or 1.5.
    """

    def calculate_score(hand):
        sum_cards = sum(hand)
        num_aces = hand.count(1)
        if num_aces >= 1 and sum_cards + 10 <= 21:
            return sum_cards + 10
        else:
            return sum_cards
    player_score = calculate_score(player)
    dealer_score = calculate_score(dealer)

    def is_natural(hand):
        return len(hand) == 2 and 1 in hand and (10 in hand)
    player_natural = is_natural(player)
    dealer_natural = is_natural(dealer)
    if player_score > 21:
        return -1.0
    elif dealer_score > 21:
        return 1.0
    elif player_score > dealer_score:
        if player_natural:
            return 1.5
        else:
            return 1.0
    elif dealer_score > player_score:
        return -1.0
    else:
        return 0.0

import numpy as np
def leaky_relu(z, a=0.01):
    """Apply the Leaky ReLU activation to every element in *z*.

    Args:
        z: A scalar, list (possibly nested) or ``numpy.ndarray`` of numbers.
        a: Optional float in [0,1) — the slope for negative inputs. Defaults to 0.01.

    Returns:
        A Python list with the same structure as *z* where each value has been transformed
        by the Leaky ReLU activation.
    """
    if isinstance(z, np.ndarray):
        activated = np.where(z >= 0, z, a * z)
        return activated.tolist()
    elif isinstance(z, list):
        return [leaky_relu(element, a) for element in z]
    elif z >= 0:
        return z
    else:
        return a * z

