def to_networkx(G: Dict[str, Any]) -> Dict[Any, List[Tuple[Any, float]]]:
    """Convert a custom graph dictionary into a plain adjacency dictionary."""
    vertex_labels = list(G['_V2I'].keys())
    adj = {u: [] for u in vertex_labels}
    for i in range(len(vertex_labels)):
        u = vertex_labels[i]
        for edge in G['_G'][i]:
            (source, target, weight) = edge
            adj[u].append((target, weight))
            if not G['is_directed']:
                adj[target].append((u, weight))
    for u in adj:
        seen = set()
        unique = []
        for (v, w) in adj[u]:
            if (v, w) not in seen:
                seen.add((v, w))
                unique.append((v, w))
        adj[u] = unique
    for u in adj:
        adj[u].sort(key=lambda x: x[0])
    return adj

