from typing import List
def ngram_log_prob(corpus: List[str], query: str, N: int) -> float:
    """Return the total log-probability of *query* under an N-gram MLE model.

    Parameters
    ----------
    corpus : list[str]
        A list of training sentences. Each sentence is a plain string; tokens
        are assumed to be separated by white-spaces.
    query : str
        The sentence whose probability should be calculated.
    N : int
        The order of the N-gram model (N ≥ 1).

    Returns
    -------
    float
        The sum of natural logarithms of the probabilities of every
        consecutive N-gram occurring in *query*, rounded to 4 decimal places.
        If any necessary N-gram is unseen in the corpus the function returns
        float('-inf').
    """
    ngram_counts = defaultdict(int)
    nminus1_counts = defaultdict(int)
    for sentence in corpus:
        tokens = sentence.split()
        if N > 1:
            padded = ['<bol>'] * (N - 1) + tokens + ['<eol>']
        else:
            padded = tokens
        for i in range(len(padded) - N + 1):
            ngram = tuple(padded[i:i + N])
            ngram_counts[ngram] += 1
            if N > 1:
                prefix = tuple(padded[i:i + N - 1])
                nminus1_counts[prefix] += 1
    if N == 1:
        total_tokens = sum(ngram_counts.values())
    else:
        total_tokens = None
    query_tokens = query.split()
    if N > 1:
        padded_query = ['<bol>'] * (N - 1) + query_tokens + ['<eol>']
    else:
        padded_query = query_tokens
    if len(padded_query) < N:
        return float('-inf')
    log_prob = 0.0
    for i in range(len(padded_query) - N + 1):
        ngram = tuple(padded_query[i:i + N])
        if ngram not in ngram_counts:
            return float('-inf')
        if N > 1:
            prefix = ngram[:-1]
            if prefix not in nminus1_counts:
                return float('-inf')
        if N == 1:
            denominator = total_tokens
        else:
            denominator = nminus1_counts[prefix]
        prob = ngram_counts[ngram] / denominator
        log_prob += math.log(prob)
    return round(log_prob, 4)

from collections import Counter
def build_fp_tree(transactions: list[list[str]], min_support: int) -> dict:
    """Construct an FP-Tree using only nested dictionaries.

    Parameters
    ----------
    transactions : list[list[str]]
        A list where each element is a list of items representing one transaction.
    min_support : int
        Minimum number of transactions an item has to appear in to be kept.

    Returns
    -------
    dict
        The root node of the FP-Tree.  Each node contains two keys:
        "support" and "children" (the latter mapping item → child-node).
    """
    freq = Counter()
    for transaction in transactions:
        unique_items = set(transaction)
        for item in unique_items:
            freq[item] += 1
    frequent_items = {item: count for (item, count) in freq.items() if count >= min_support}
    if not frequent_items:
        return {'support': 0, 'children': {}}
    global_order = sorted(frequent_items.keys(), key=lambda x: (-frequent_items[x], x))
    order_map = {item: idx for (idx, item) in enumerate(global_order)}
    root = {'support': 0, 'children': {}}
    for transaction in transactions:
        unique_filtered = [item for item in set(transaction) if item in frequent_items]
        if not unique_filtered:
            continue
        ordered_transaction = sorted(unique_filtered, key=lambda x: order_map[x])
        current_node = root
        for item in ordered_transaction:
            if item in current_node['children']:
                current_node = current_node['children'][item]
                current_node['support'] += 1
            else:
                new_node = {'support': 1, 'children': {}}
                current_node['children'][item] = new_node
                current_node = new_node
    return root

