def env_stats(actions: list, observations: list) -> dict:
    """Compute statistics for collections of actions and observations.

    The function inspects *actions* and *observations* and returns a dictionary
    containing information about dimensionality, data type (discrete or
    continuous) and the unique values appearing in every dimension.

    Parameters
    ----------
    actions : list
        A list with the actions that were taken.  Each element is either a
        scalar or an iterable of scalars (for multi-dimensional spaces).
    observations : list
        A list with the corresponding observations.  Same structural
        requirements as *actions*.

    Returns
    -------
    dict
        A dictionary with the keys described in the task description.
    """

    def process_list(lst):
        is_tuple = any((isinstance(elem, (list, tuple, np.ndarray)) for elem in lst))
        if is_tuple:
            if not lst:
                dim = 0
            else:
                first_elem = lst[0]
                if isinstance(first_elem, (list, tuple, np.ndarray)):
                    dim = len(first_elem)
                else:
                    dim = 1
        else:
            dim = 1
        flattened = []
        for elem in lst:
            if isinstance(elem, (list, tuple, np.ndarray)):
                flattened.extend(elem)
            else:
                flattened.append(elem)
        is_continuous = any((isinstance(x, float) and (not x.is_integer()) for x in flattened))
        if dim == 0:
            per_dim_counts = []
            per_dim_ids = []
        elif dim == 1:
            unique = sorted(list(set(flattened)))
            per_dim_counts = [len(unique)]
            per_dim_ids = [unique]
        else:
            per_dim_values = [[] for _ in range(dim)]
            for elem in lst:
                for d in range(dim):
                    per_dim_values[d].append(elem[d])
            per_dim_counts = []
            per_dim_ids = []
            for d in range(dim):
                unique = sorted(list(set(per_dim_values[d])))
                per_dim_counts.append(len(unique))
                per_dim_ids.append(unique)
        return {'is_tuple': is_tuple, 'dim': dim, 'is_continuous': is_continuous, 'per_dim_counts': per_dim_counts, 'per_dim_ids': per_dim_ids}
    action_stats = process_list(actions)
    obs_stats = process_list(observations)
    result = {'tuple_actions': action_stats['is_tuple'], 'tuple_observations': obs_stats['is_tuple'], 'multidim_actions': action_stats['dim'] > 1, 'multidim_observations': obs_stats['dim'] > 1, 'continuous_actions': action_stats['is_continuous'], 'continuous_observations': obs_stats['is_continuous'], 'n_actions_per_dim': action_stats['per_dim_counts'], 'n_obs_per_dim': obs_stats['per_dim_counts'], 'action_dim': action_stats['dim'], 'obs_dim': obs_stats['dim'], 'action_ids': action_stats['per_dim_ids'], 'obs_ids': obs_stats['per_dim_ids']}
    return result

