def bayesian_linear_regression_pred(X: np.ndarray, y: np.ndarray, X_new: np.ndarray, mu: float | np.ndarray=0.0, sigma: float=1.0, V: float | np.ndarray | None=None, fit_intercept: bool=True) -> list[float]:
    """Bayesian linear regression with known variance.

    The function must return the MAP predictive means for every row in
    `X_new`.  See the task description for full details.
    """
    if fit_intercept:
        X_proc = np.hstack((np.ones((X.shape[0], 1)), X))
        X_new_proc = np.hstack((np.ones((X_new.shape[0], 1)), X_new))
    else:
        X_proc = X
        X_new_proc = X_new
    n_features = X_proc.shape[1]
    if V is None:
        V = np.eye(n_features)
    elif isinstance(V, (float, int)):
        V = V * np.eye(n_features)
    elif isinstance(V, np.ndarray):
        if V.ndim == 1:
            V = np.diag(V)
        elif V.shape != (n_features, n_features):
            raise ValueError('V must be a square matrix of size n_features x n_features')
    else:
        raise ValueError('V must be None, float, or ndarray')
    V_inv = np.linalg.inv(V)
    if isinstance(mu, (float, int)):
        mu = np.full(n_features, mu)
    elif isinstance(mu, np.ndarray):
        if mu.shape[0] != n_features:
            raise ValueError('mu must be a scalar or vector of length n_features')
    else:
        raise ValueError('mu must be a scalar or ndarray')
    term1 = V_inv @ mu
    term2 = X_proc.T @ y
    numerator = term1 + term2
    sum_matrix = V_inv + X_proc.T @ X_proc
    Sigma_p = np.linalg.inv(sum_matrix)
    mu_p = Sigma_p @ numerator
    predictions = X_new_proc @ mu_p
    rounded_predictions = [round(pred, 4) for pred in predictions]
    return rounded_predictions

