def rbf_kernel(X: list[list[int | float]], Y: list[list[int | float]] | None=None, sigma: float | list[float] | None=None) -> list[list[float]]:
    """Compute the Radial Basis Function (RBF) kernel matrix.

    The function should follow the specifications given in the task
    description. It must return -1 on invalid input, otherwise a nested list
    containing the kernel matrix rounded to four decimal places.
    """
    try:
        X_np = np.array(X, dtype=float)
    except:
        return -1
    if X_np.ndim != 2 or X_np.shape[0] == 0:
        return -1
    C = X_np.shape[1]
    N = X_np.shape[0]
    if Y is not None:
        try:
            Y_np = np.array(Y, dtype=float)
        except:
            return -1
        if Y_np.ndim != 2 or Y_np.shape[0] == 0:
            return -1
        if Y_np.shape[1] != C:
            return -1
    else:
        Y_np = X_np
    M = Y_np.shape[0]
    if sigma is None:
        sigma_val = np.sqrt(C / 2)
        sigma_squared = sigma_val ** 2
    elif isinstance(sigma, float):
        if sigma <= 0:
            return -1
        sigma_squared = sigma ** 2
    elif isinstance(sigma, list):
        sigma_list = np.array(sigma, dtype=float)
        if len(sigma_list) != C or np.any(sigma_list <= 0):
            return -1
        sigma_squared = sigma_list ** 2
    else:
        return -1
    squared_diffs = (X_np[:, None, :] - Y_np) ** 2
    if isinstance(sigma_squared, np.ndarray):
        sigma_squared = sigma_squared.reshape(1, 1, -1)
        scaled_diffs = squared_diffs / sigma_squared
    else:
        scaled_diffs = squared_diffs / sigma_squared
    sum_scaled = np.sum(scaled_diffs, axis=2)
    kernel = np.exp(-0.5 * sum_scaled)
    kernel_rounded = np.round(kernel, 4)
    result = kernel_rounded.tolist()
    return result

