from collections import Counter
def decision_stump(X: list[list[float]], y: list[int]) -> tuple:
    """Find a decision stump that maximises information gain."""

    def entropy(labels):
        if not labels:
            return 0.0
        counts = Counter(labels)
        total = len(labels)
        ent = 0.0
        for (label, count) in counts.items():
            p = count / total
            ent -= p * np.log2(p)
        return ent

    def majority_label(labels):
        if not labels:
            return None
        counts = Counter(labels)
        max_count = max(counts.values())
        candidates = [k for (k, v) in counts.items() if v == max_count]
        return min(candidates)
    n_samples = len(y)
    if n_samples == 0:
        return (-1, None, None, None)
    n_features = len(X[0]) if n_samples > 0 else 0
    H_parent = entropy(y)
    if H_parent == 0:
        majority = majority_label(y)
        return (-1, None, majority, majority)
    best_gain = -float('inf')
    best_j = -1
    best_t = None
    best_left_label = None
    best_right_label = None
    for j in range(n_features):
        Xj = [X[i][j] for i in range(n_samples)]
        unique_sorted = sorted(list(set(Xj)))
        if len(unique_sorted) < 2:
            continue
        midpoints = []
        for i in range(len(unique_sorted) - 1):
            mid = (unique_sorted[i] + unique_sorted[i + 1]) / 2
            midpoints.append(mid)
        for t in midpoints:
            left_indices = [i for i in range(n_samples) if X[i][j] <= t]
            right_indices = [i for i in range(n_samples) if X[i][j] > t]
            if len(left_indices) == 0 or len(right_indices) == 0:
                continue
            left_labels = [y[i] for i in left_indices]
            right_labels = [y[i] for i in right_indices]
            H_left = entropy(left_labels)
            H_right = entropy(right_labels)
            p_left = len(left_labels) / n_samples
            p_right = len(right_labels) / n_samples
            gain = H_parent - p_left * H_left - p_right * H_right
            if gain > best_gain:
                best_gain = gain
                best_j = j
                best_t = t
                best_left_label = majority_label(left_labels)
                best_right_label = majority_label(right_labels)
            elif gain == best_gain:
                if j < best_j:
                    best_j = j
                    best_t = t
                    best_left_label = majority_label(left_labels)
                    best_right_label = majority_label(right_labels)
                elif j == best_j:
                    if t < best_t:
                        best_t = t
                        best_left_label = majority_label(left_labels)
                        best_right_label = majority_label(right_labels)
    if best_gain > 0:
        best_t_rounded = round(best_t, 4)
        return (best_j, best_t_rounded, best_left_label, best_right_label)
    else:
        majority = majority_label(y)
        return (-1, None, majority, majority)

