def to_categorical(labels: list | tuple | 'np.ndarray', num_classes: int | None=None) -> list[list[int]]:
    """Convert integer class labels to one-hot encoded format.

    Args:
        labels: 1-D sequence of non-negative integer labels.
        num_classes: Total number of classes. If *None*, infer as
            ``max(labels) + 1``.

    Returns:
        A list of lists representing the one-hot encoded labels, or -1
        when the input is invalid (negative label or `num_classes` too
        small).
    """
    if not labels:
        return []
    for label in labels:
        if label < 0:
            return -1
    max_label = max(labels)
    if num_classes is not None:
        if num_classes < max_label + 1:
            return -1
        for label in labels:
            if label >= num_classes:
                return -1
    else:
        num_classes = max_label + 1
    result = []
    for label in labels:
        row = [0] * num_classes
        row[label] = 1
        result.append(row)
    return result

import numpy as np
def expected_sarsa_update(q_table: list[list[float]], state: int, action: int, reward: float, next_state: int | None, epsilon: float, lr: float, gamma: float) -> list[list[float]]:
    q = [row.copy() for row in q_table]
    num_actions = len(q[0]) if q else 0
    if next_state is not None:
        q_next = q[next_state]
        max_val = max(q_next)
        greedy_action = next((a for (a, val) in enumerate(q_next) if val == max_val), None)
        expected_value = 0.0
        for a in range(num_actions):
            if a == greedy_action:
                prob = 1.0 - epsilon + epsilon / num_actions
            else:
                prob = epsilon / num_actions
            expected_value += prob * q_next[a]
        target = reward + gamma * expected_value
    else:
        target = reward
    current_q = q[state][action]
    new_q = current_q + lr * (target - current_q)
    q[state][action] = new_q
    for s in range(len(q)):
        for a in range(len(q[s])):
            q[s][a] = round(q[s][a], 4)
    return q

