    min_samples : int
        Minimum number of samples (including the point itself) required to form
        a dense region.

    Returns
    -------
    list[int]
        Cluster labels for each sample. Noise points are labeled ‑1 and cluster
        indices start at 0 and increase sequentially in discovery order.
    """
    if data.shape[0] == 0:
        return []
    n = data.shape[0]
    labels = [-1] * n
    cluster_id = 0
    neighbors = []
    for i in range(n):
        nbrs = []
        for j in range(n):
            if i != j:
                dist = np.linalg.norm(data[i] - data[j])
                if dist <= eps:
                    nbrs.append(j)
        neighbors.append(nbrs)
    for i in range(n):
        if labels[i] != -1:
            continue
        if len(neighbors[i]) + 1 >= min_samples:
            cluster_label = None
            for nbr in neighbors[i]:
                if labels[nbr] != -1:
                    cluster_label = labels[nbr]
                    break
            if cluster_label is not None:
                labels[i] = cluster_label
                queue = [i]
                while queue:
                    current = queue.pop(0)
                    for nbr in neighbors[current]:
                        if labels[nbr] == -1:
                            labels[nbr] = cluster_label
                            queue.append(nbr)
            else:
                labels[i] = cluster_id
                queue = [i]
                while queue:
                    current = queue.pop(0)
                    for nbr in neighbors[current]:
                        if labels[nbr] == -1:
                            labels[nbr] = cluster_id
                            queue.append(nbr)
                cluster_id += 1
    return labels.tolist()

import numpy as np
def regression_tree_predict(X_train: list[float | int], y_train: list[float | int], X_test: list[float | int], max_depth: int=3) -> list[float]:
    """Fit a univariate regression tree of depth *max_depth* and predict.

    Parameters
    ----------
    X_train : list[float | int]
        1-D feature values for the training set.
    y_train : list[float | int]
        Continuous target values for the training set.
    X_test : list[float | int]
        1-D feature values for which predictions are required.
    max_depth : int, default 3
        Maximum depth of the binary tree (root has depth 0).

    Returns
    -------
    list[float]
        Predictions for every element in *X_test*.
    """
    if not X_train:
        return []

    def find_best_split(X, y):
        unique_splits = np.unique(X)
        min_sse = float('inf')
        best_s = None
        for s in unique_splits:
            left_y = [y[i] for i in range(len(X)) if X[i] <= s]
            right_y = [y[i] for i in range(len(X)) if X[i] > s]
            if len(left_y) == 0 or len(right_y) == 0:
                continue
            sse_left = sum(((y_val - np.mean(left_y)) ** 2 for y_val in left_y))
            sse_right = sum(((y_val - np.mean(right_y)) ** 2 for y_val in right_y))
            total_sse = sse_left + sse_right
            if total_sse < min_sse:
                min_sse = total_sse
                best_s = s
        return best_s

    def build_tree(X, y, current_depth, max_depth):
        if current_depth >= max_depth or len(y) < 2:
            return {'value': np.mean(y)}
        else:
            best_s = find_best_split(X, y)
            if best_s is None:
                return {'value': np.mean(y)}
            else:
                left_indices = [i for i in range(len(X)) if X[i] <= best_s]
                right_indices = [i for i in range(len(X)) if X[i] > best_s]
                left_X = [X[i] for i in left_indices]
                left_y = [y[i] for i in left_indices]
                right_X = [X[i] for i in right_indices]
                right_y = [y[i] for i in right_indices]
                left_child = build_tree(left_X, left_y, current_depth + 1, max_depth)
                right_child = build_tree(right_X, right_y, current_depth + 1, max_depth)
                return {'split': best_s, 'left': left_child, 'right': right_child}

    def predict(x, node):
        if 'split' not in node:
            return node['value']
        else:
            s = node['split']
            if x <= s:
                return predict(x, node['left'])
            else:
                return predict(x, node['right'])
    tree = build_tree(X_train, y_train, 0, max_depth)
    predictions = []
    for x in X_test:
        predictions.append(predict(x, tree))
    return predictions

from collections import defaultdict
class Node:

    def __init__(self, item):
        self.item = item
        self.count = 0
        self.parent = None
        self.children = {}
        self.next = None
class FPTree:

    def __init__(self):
        self.root = Node(None)
        self.root.parent = None
        self.root.children = {}
        self.root.next = None
def fp_growth(transactions, min_sup):
    """Mine all frequent item-sets using the FP-Growth algorithm.

    Parameters
    ----------
    transactions : Iterable[Iterable[Hashable]]
        A collection of transactions.  Each transaction is an iterable containing hashable items.
    min_sup : int
        Minimum number of occurrences an item-set must have to be considered frequent.

    Returns
    -------
    list[tuple]
        All frequent item-sets sorted 1) by length, 2) lexicographically.  Every
        tuple itself is sorted lexicographically.
    """
    if not transactions:
        return []
    frequency = defaultdict(int)
    for transaction in transactions:
        for item in transaction:
            frequency[item] += 1
    filtered_transactions = []
    for transaction in transactions:
        filtered = [item for item in transaction if frequency[item] >= min_sup]
        sorted_transaction = sorted(filtered, key=lambda x: (-frequency[x], x))
        filtered_transactions.append(sorted_transaction)
    fp_tree = FPTree()
    header_table = {}
    for trans in filtered_transactions:
        current = fp_tree.root
        for item in trans:
            if item in current.children:
                current = current.children[item]
                current.count += 1
            else:
                new_node = Node(item)
                new_node.parent = current
                current.children[item] = new_node
                if item not in header_table:
                    header_table[item] = new_node
                else:
                    last = header_table[item]
                    while last.next:
                        last = last.next
                    last.next = new_node
                current = new_node
                current.count = 1
    result = []
    if not header_table:
        return result
    mine(header_table, (), min_sup, result, frequency)
    result.sort(key=lambda x: (len(x), x))
    return result

def predict_sample(root: 'Node', features: list | tuple, task_type: str):
    """Return the prediction for one sample by traversing the decision tree.

    Args:
        root (Node): The root node of the decision tree.
        features (list | tuple): The feature values of the sample.
        task_type (str): Either 'classification' or 'regression'.

    Returns:
        The value stored in the reached leaf node.
    """
    if hasattr(root, 'y'):
        return root.y
    if task_type == 'classification':
        feature_index = root.label
        feature_value = features[feature_index]
        for child in root.child:
            if child.x == feature_value:
                return predict_sample(child, features, task_type)
        return predict_sample(root.child[1], features, task_type)
    else:
        feature_index = root.label
        feature_value = features[feature_index]
        if feature_value <= root.s:
            next_node = root.child[0]
        else:
            next_node = root.child[1]
        return predict_sample(next_node, features, task_type)

import math
from collections import defaultdict
import numpy as np
def maxent_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, epsilon: float=0.001, n_iter: int=100) -> list[int]:
    empirical_counts = defaultdict(int)
    for i in range(X_train.shape[0]):
        x = X_train[i]
        y = y_train[i]
        for j in range(x.shape[0]):
            v = x[j]
            feature = (j, v, y)
            empirical_counts[feature] += 1
    weights = {feature: 0.0 for feature in empirical_counts}
    possible_ys = np.unique(y_train)
    for _ in range(n_iter):
        expected_counts = defaultdict(float)
        for x in X_train:
            sum_weights = {}
            for y in possible_ys:
                s = 0.0
                for j in range(x.shape[0]):
                    v = x[j]
                    feature = (j, v, y)
                    if feature in weights:
                        s += weights[feature]
                sum_weights[y] = s
            Z = 0.0
            for y in possible_ys:
                Z += math.exp(sum_weights[y])
            for y in possible_ys:
                if Z == 0:
                    p = 0.0
                else:
                    p = math.exp(sum_weights[y]) / Z
                for j in range(x.shape[0]):
                    v = x[j]
                    feature = (j, v, y)
                    if feature in weights:
                        expected_counts[feature] += p
        max_delta = 0.0
        for feature in weights:
            emp = empirical_counts[feature]
            exp = expected_counts.get(feature, 0.0)
            if exp < 1e-10:
                exp = 1e-10
            delta = (emp - exp) / exp
            weights[feature] += delta
            if abs(delta) > max_delta:
                max_delta = abs(delta)
        if max_delta < epsilon:
            break
    predicted_labels = []
    for x_test in X_test:
        sum_weights = {}
        for y in possible_ys:
            s = 0.0
            for j in range(x_test.shape[0]):
                v = x_test[j]
                feature = (j, v, y)
                if feature in weights:
                    s += weights[feature]
            sum_weights[y] = s
        Z = sum((math.exp(s) for s in sum_weights.values()))
        if Z == 0:
            predicted_y = possible_ys[0]
        else:
            max_p = -1
            predicted_y = possible_ys[0]
            for y in possible_ys:
                p = math.exp(sum_weights[y]) / Z
                if p > max_p:
                    max_p = p
                    predicted_y = y
        predicted_labels.append(predicted_y)
    return predicted_labels

from collections import Counter
from typing import Iterable, Tuple, List, Set, Any
def statistic_key_word(data: List[Tuple[Any, Iterable[str]]], cut_off: int | None=None) -> Tuple[List[Tuple[str, float]], Set[str]]:
    word_counts = Counter()
    for document in data:
        (_, words) = document
        unique_words = set(words)
        for word in unique_words:
            word_counts[word] += 1
    total_documents = len(data)
    word_ratios = []
    if total_documents != 0:
        for (word, count) in word_counts.items():
            ratio = count / total_documents
            rounded_ratio = round(ratio, 4)
            word_ratios.append((word, rounded_ratio))
    sorted_word_ratios = sorted(word_ratios, key=lambda x: (-x[1], x[0]))
    if cut_off is None:
        top_words = {word for (word, ratio) in sorted_word_ratios}
    elif cut_off == 0:
        top_words = set()
    else:
        top_words = set()
        for (word, ratio) in sorted_word_ratios[:cut_off]:
            top_words.add(word)
    return (sorted_word_ratios, top_words)

import numpy as np
def local_outlier_factor(data, k, epsilon=1.5):
    n = data.shape[0]
    if n == 0:
        return ([], [])
    distance_matrix = np.sqrt(np.sum((data[:, None, :] - data[None, :, :]) ** 2, axis=2))
    N_k = []
    k_distances = np.zeros(n)
    for p in range(n):
        dists = distance_matrix[p]
        neighbors = []
        for q in range(n):
            if q != p:
                neighbors.append((dists[q], q))
        neighbors.sort(key=lambda x: x[0])
        k_neighbors = neighbors[:k]
        N_k_p = [q for (d, q) in k_neighbors]
        N_k.append(N_k_p)
        if k_neighbors:
            k_dist_p = k_neighbors[-1][0]
        else:
            k_dist_p = 0.0
        k_distances[p] = k_dist_p
    LRD = np.zeros(n)
    for p in range(n):
        sum_reach = 0.0
        for q in N_k[p]:
            d_pq = distance_matrix[p][q]
            k_dist_q = k_distances[q]
            reach_dist = max(k_dist_q, d_pq)
            sum_reach += reach_dist
        if sum_reach == 0:
            LRD[p] = float('inf')
        else:
            LRD[p] = k / sum_reach
    LOF = np.zeros(n)
    for p in range(n):
        if LRD[p] == float('inf'):
            LOF[p] = 1.0
        else:
            sum_LRD = 0.0
            for q in N_k[p]:
                sum_LRD += LRD[q]
            avg_LRD = sum_LRD / k
            LOF[p] = avg_LRD / LRD[p]
    rounded_LOF = np.round(LOF, 4)
    outlier_indices = [i for i in range(n) if rounded_LOF[i] > epsilon]
    outlier_indices.sort()
    return (rounded_LOF.tolist(), outlier_indices)
