import numpy as np
def backward_prob(A: list[list[float]], B: list[list[float]], pi: list[float], obs: list[int]) -> float:
    if len(A) == 0 or len(B) == 0 or len(pi) == 0 or (len(obs) == 0):
        return 0.0
    N = len(pi)
    T = len(obs)
    if T == 0:
        return 0.0
    beta = [[0.0 for _ in range(N)] for _ in range(T)]
    for t in range(T - 1, -1, -1):
        if t == T - 1:
            for i in range(N):
                beta[t][i] = 1.0
        else:
            for i in range(N):
                sum_val = 0.0
                for j in range(N):
                    sum_val += A[i][j] * B[j][obs[t + 1]] * beta[t + 1][j]
                beta[t][i] = sum_val
    total = 0.0
    for i in range(N):
        total += pi[i] * B[i][obs[0]] * beta[0][i]
    return round(total, 6)
assert backward_prob([[0.7,0.3],[0.4,0.6]], [[0.1,0.4,0.5],[0.6,0.3,0.1]], [0.6,0.4], [0,1,2]) == 0.033612, "test case failed: example sequence"
