import numpy as np
def leaky_relu(z, a=0.01):
    """Apply the Leaky ReLU activation to every element in *z.

    Args:
        z: A scalar, list (possibly nested) or ``numpy.ndarray`` of numbers.
        a: Optional float in [0,1) — the slope for negative inputs. Defaults to 0.01.

    Returns:
        A Python list with the same structure as *z where each value has been transformed
        by the Leaky ReLU activation.
    """

    def process_array(x, a):
        return np.array([process_element(e, a) for e in x])

    def process_element(x, a):
        if isinstance(x, (list, np.ndarray)):
            return [process_element(e, a) for e in x]
        elif x >= 0:
            return x
        else:
            return a * x

    def process_list(x, a):
        return [process_element(e, a) for e in x]
    if isinstance(z, np.ndarray):
        return process_array(z, a)
    else:
        return process_list(z, a)
assert leaky_relu([-2, -1, 0, 1, 2]) == [-0.02, -0.01, 0.0, 1.0, 2.0], "failed on default slope, 1-D list"
assert leaky_relu(np.array([-5.0, 5.0]), 0.2) == [-1.0, 5.0], "failed on numpy input, custom slope"
assert leaky_relu(np.array([[[-1]]]), 0.5) == [[[-0.5]]], "failed on 3-D array"
assert leaky_relu(0) == 0.0, "failed on scalar zero"
assert leaky_relu(-4, 0.25) == -1.0, "failed on scalar negative"
assert leaky_relu(3.3) == 3.3, "failed on scalar positive"
assert leaky_relu([[0]]) == [[0.0]], "failed on zero inside nested list"
