import numpy as np
def glm_links():
    """Construct and return standard GLM link functions.

    Returns
    -------
    dict
        A three-entry dictionary (identity, log, logit) where each entry is a
        dictionary containing callables for the link, its inverse and its
        derivative with respect to μ. All functions must work with numpy
        scalars as well as 1-D/2-D numpy arrays via element-wise operations.
    """

    def identity(mu):
        return mu

    def inv_identity(eta):
        return eta

    def identity_derivative(mu):
        return 1.0

    def log(mu):
        return np.log(mu)

    def inv_log(eta):
        return np.exp(eta)

    def log_derivative(mu):
        return 1.0 / mu

    def logit(mu):
        return np.log(mu / (1 - mu))

    def inv_logit(eta):
        return np.exp(eta) / (1 + np.exp(eta))

    def logit_derivative(mu):
        return 1.0 / (mu * (1 - mu))
    return {'identity': {'link': identity, 'inv_link': inv_identity, 'derivative': identity_derivative}, 'log': {'link': log, 'inv_link': inv_log, 'derivative': log_derivative}, 'logit': {'link': logit, 'inv_link': inv_logit, 'derivative': logit_derivative}}

import numpy as np
def forward_algorithm(S: list[float], A: list[list[float]], B: list[list[float]], observations: list[int]) -> float:
    """Forward algorithm for Hidden Markov Models.

    Args:
        S (list[float]): Initial state probabilities.
        A (list[list[float]]): State–transition probabilities.
        B (list[list[float]]): Emission probabilities.
        observations (list[int]): Observation index sequence.

    Returns:
        float: Sequence likelihood rounded to 4 decimals, or −1 on invalid input.
    """
    n = len(S)
    m = len(B[0])
    if not S or not A or (not B) or (not observations):
        return -1
    if len(A) != n or any((len(row) != n for row in A)):
        return -1
    if len(B) != n or any((len(row) != m for row in B)):
        return -1
    for obs in observations:
        if not 0 <= obs < m:
            return -1
    T = len(observations)
    alpha = np.zeros((T, n))
    for i in range(n):
        alpha[0, i] = S[i] * B[i, observations[0]]
    for t in range(1, T):
        for j in range(n):
            for i in range(n):
                alpha[t, j] += alpha[t - 1, i] * A[i, j]
            alpha[t, j] *= B[j, observations[t]]
    likelihood = np.sum(alpha[T - 1, :])
    return round(likelihood, 4)

from collections import Counter, defaultdict
def fp_growth(transactions: list[list[str]], min_support: int) -> list[list[str]]:
    """Discover every frequent item-set in *transactions* with FP-Growth.

    A *transaction* is represented by a list of items (strings).  `min_support`
    is the minimum number of transactions an item-set has to appear in.  The
    function returns **all** frequent item-sets where
        support(itemset) >= min_support.

    The result must be deterministic:
      • Inside each item-set the items have to be sorted alphabetically.
      • The outer list has to be sorted by `(len(itemset), itemset)`.
    If *transactions* is empty or no item-set meets the threshold return an
    empty list.
    """
    if not transactions:
        return []
    item_counts = Counter()
    for transaction in transactions:
        item_counts.update(transaction)
    frequent_items = {item for (item, count) in item_counts.items() if count >= min_support}
    if not frequent_items:
        return []

    def build_fp_tree(transactions, frequent_items):
        header = defaultdict(int)
        tree = {}
        for transaction in transactions:
            sorted_transaction = [item for item in transaction if item in frequent_items]
            if not sorted_transaction:
                continue
            current_node = tree
            for item in sorted_transaction:
                if item not in current_node:
                    current_node[item] = {'child': {}, 'parent': current_node, 'count': 1}
                    header[item] = 1
                else:
                    current_node[item]['count'] += 1
                current_node = current_node[item]['child']
        return (tree, header)
    (tree, header) = build_fp_tree(transactions, frequent_items)

    def mine_tree(tree, header, min_support, frequent_itemsets):
        if not header:
            return
        for item in sorted(header.keys()):
            paths = []
            node = tree
            path = []
            while True:
                if item in node:
                    path.append(item)
                    break
                else:
                    found = False
                    for k in node:
                        if k in header:
                            node = node[k]['child']
                            path.append(k)
                            found = True
                            break
                    if not found:
                        break
            if item in node:
                paths.append(path)
            conditional_pattern_base = defaultdict(int)
            for path in paths:
                for i in range(len(path) - 1):
                    itemset = tuple(path[i:i + 2])
                    conditional_pattern_base[itemset] += 1
            conditional_transactions = []
            for transaction in transactions:
                conditional_transaction = [item for item in transaction if item in frequent_items]
                path_found = False
                for path in paths:
                    if all((item in conditional_transaction for item in path)):
                        path_found = True
                        break
                if path_found:
                    conditional_transactions.append(conditional_transaction)
            (conditional_tree, conditional_header) = build_fp_tree(conditional_transactions, frequent_items)
            if conditional_header:
                new_frequent_itemsets = mine_tree(conditional_tree, conditional_header, min_support, frequent_itemsets)
                frequent_itemsets.extend(new_frequent_itemsets)
            frequent_itemsets.append(sorted([item for item in path if item in frequent_items]))
        return frequent_itemsets
    frequent_itemsets = []
    frequent_itemsets = mine_tree(tree, header, min_support, frequent_itemsets)
    unique_itemsets = []
    for itemset in frequent_itemsets:
        if itemset not in unique_itemsets:
            unique_itemsets.append(itemset)
    unique_itemsets.sort(key=lambda x: (len(x), x))
    return unique_itemsets

import numpy as np
def decision_tree_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, max_depth: int | None=None) -> list[int]:
    """Build a CART decision tree on (X_train, y_train) and predict labels for X_test.

    Parameters
    ----------
    X_train : np.ndarray
        Training feature matrix of shape (n_samples, n_features).
    y_train : np.ndarray
        Integer class labels for the training data, shape (n_samples,).
    X_test : np.ndarray
        Feature matrix to classify, shape (m_samples, n_features).
    max_depth : int | None, optional
        Maximum allowed depth of the tree. If None the depth is unlimited.

    Returns
    -------
    list[int]
        Predicted class label for each row in X_test.
    """

    class Node:

        def __init__(self, feature=None, threshold=None, left=None, right=None, label=None, depth=0):
            self.feature = feature
            self.threshold = threshold
            self.left = left
            self.right = right
            self.label = label
            self.depth = depth

    def gini_impurity(y):
        if len(np.unique(y)) == 1:
            return 0
        counts = np.bincount(y)
        probabilities = counts / len(y)
        return 1 - np.sum(probabilities ** 2)

    def find_best_split(X, y):
        best_feature = None
        best_threshold = None
        best_gain = -1
        n_features = X.shape[1]
        for feature in range(n_features):
            feature_values = np.unique(X[:, feature])
            for i in range(len(feature_values) - 1):
                threshold = (feature_values[i] + feature_values[i + 1]) / 2
                left_indices = X[:, feature] <= threshold
                right_indices = ~left_indices
                if np.sum(left_indices) == 0 or np.sum(right_indices) == 0:
                    continue
                y_left = y[left_indices]
                y_right = y[right_indices]
                gini_parent = gini_impurity(y)
                gini_left = gini_impurity(y_left)
                gini_right = gini_impurity(y_right)
                gain = gini_parent - len(y_left) / len(y) * gini_left - len(y_right) / len(y) * gini_right
                if gain > best_gain:
                    best_gain = gain
                    best_feature = feature
                    best_threshold = threshold
        return (best_feature, best_threshold)

    def build_tree(X, y, depth):
        if depth == max_depth or len(np.unique(y)) == 1:
            return Node(label=np.argmax(np.bincount(y)))
        (feature, threshold) = find_best_split(X, y)
        if feature is None:
            return Node(label=np.argmax(np.bincount(y)))
        left_indices = X[:, feature] <= threshold
        right_indices = ~left_indices
        if np.sum(left_indices) == 0 or np.sum(right_indices) == 0:
            return Node(label=np.argmax(np.bincount(y)))
        left_child = build_tree(X[left_indices], y[left_indices], depth + 1)
        right_child = build_tree(X[right_indices], y[right_indices], depth + 1)
        return Node(feature=feature, threshold=threshold, left=left_child, right=right_child, depth=depth)

    def predict_row(row, node):
        if node.label is not None:
            return node.label
        if row[node.feature] <= node.threshold:
            return predict_row(row, node.left)
        else:
            return predict_row(row, node.right)
    tree = build_tree(X_train, y_train, 0)
    predictions = [predict_row(row, tree) for row in X_test]
    return predictions

