def to_networkx(G: Dict[str, Any]) -> Dict[Any, List[Tuple[Any, float]]]:
    """Convert a custom graph dictionary into a plain adjacency dictionary.

    Parameters
    ----------
    G : dict
        A graph represented as a dictionary with the keys:
          * 'is_directed' (bool)
          * '_V2I'        (dict mapping vertex label -> index)
          * '_G'          (list of adjacency lists, each edge is a
                           tuple (source_label, target_label, weight))

    Returns
    -------
    dict
        A dictionary where each key is a vertex label and the value is a list
        of `(neighbour, weight)` tuples.  For undirected graphs every edge
        appears once in each direction and the neighbour lists are sorted
        alphabetically.
    """
    if not G['is_directed']:
        adj = {}
        for u in G['_V2I']:
            adj[u] = []
        for edge in G['_G']:
            (source_label, target_label, weight) = edge
            if source_label not in adj:
                adj[source_label] = []
            if target_label not in adj:
                adj[target_label] = []
            adj[source_label].append((target_label, weight))
            adj[target_label].append((source_label, weight))
        for u in adj:
            adj[u].sort(key=lambda x: x[0])
    else:
        adj = {}
        for u in G['_V2I']:
            adj[u] = []
        for edge in G['_G']:
            (source_label, target_label, weight) = edge
            if source_label not in adj:
                adj[source_label] = []
            adj[source_label].append((target_label, weight))
        for u in adj:
            adj[u].sort(key=lambda x: x[0])
    return adj

import numpy as np
def mel_filterbank(N: int, n_filters: int=20, fs: int=44000, min_freq: int=0, max_freq: int | None=None, normalize: bool=True) -> list[list[float]]:
    """Build a Mel filterbank transformation matrix.

    The returned matrix has *n_filters* rows and *N//2 + 1* columns.  Each row
    is a triangular filter defined on the Mel scale.  See the detailed task
    description for the exact algorithm that has to be implemented.
    """
    if max_freq is None:
        max_freq = fs / 2
    mel_min = mel = 2595 * np.log10(1 + min_freq / 700)
    mel_max = mel = 2595 * np.log10(1 + max_freq / 700)
    mel_axis = np.linspace(mel_min, mel_max, n_filters + 2)
    freq_axis = 700 * (10 ** (mel_axis / 2595) - 1)
    f_low = freq_axis[:-2]
    f_mid = freq_axis[1:-1]
    f_high = freq_axis[2:]
    filterbank = np.zeros((n_filters, N // 2 + 1))
    for i in range(n_filters):
        for j in range(N // 2 + 1):
            if freq_axis[i] <= freq_axis[i + 1] and freq_axis[i] <= freq_axis[i + 2]:
                filterbank[i, j] = (freq_axis[i + 1] - freq_axis[i]) * (freq_axis[i + 2] - freq_axis[i]) / (freq_axis[i + 1] - freq_axis[i] + (freq_axis[i + 2] - freq_axis[i]))
            elif freq_axis[i + 1] <= freq_axis[i] and freq_axis[i + 1] <= freq_axis[i + 2]:
                filterbank[i, j] = (freq_axis[i + 1] - freq_axis[i]) * (freq_axis[i + 2] - freq_axis[i]) / (freq_axis[i + 1] - freq_axis[i] + (freq_axis[i + 2] - freq_axis[i]))
            elif freq_axis[i + 2] <= freq_axis[i] and freq_axis[i + 2] <= freq_axis[i + 1]:
                filterbank[i, j] = (freq_axis[i + 1] - freq_axis[i]) * (freq_axis[i + 2] - freq_axis[i]) / (freq_axis[i + 1] - freq_axis[i] + (freq_axis[i + 2] - freq_axis[i]))
            else:
                filterbank[i, j] = 0
    if normalize:
        for i in range(n_filters):
            filterbank[i, :] = filterbank[i, :] * 2 / (f_high[i] - f_low[i])
    return [[round(x, 4) for x in row] for row in filterbank.tolist()]

from typing import Any, Dict, Iterable, Tuple as PyTuple
class Space:
    pass
class Box(Space):

    def __init__(self, low: float, high: float, shape: PyTuple[int, ...]):
        self.low = low
        self.high = high
        self.shape = shape
class Tuple(Space):

    def __init__(self, spaces: Iterable[Space]):
        self.spaces = tuple(spaces)
class Dict(Space):

    def __init__(self, spaces: Dict[str, Space]):
        self.spaces = dict(spaces)
class Env:
    """Tiny environment that only stores two spaces."""

    def __init__(self, action_space: Space, observation_space: Space):
        self.action_space = action_space
        self.observation_space = observation_space
def is_continuous(env: Env, tuple_action: bool, tuple_obs: bool):
    """Determine whether the given environment's spaces are continuous.

    A space is *continuous* if it is an instance of `Box`. For composite spaces
    (`Tuple` or `Dict`) the space is continuous only if **all** its sub-spaces
    are `Box`.

    Args:
        env:          Environment exposing `action_space` and `observation_space`.
        tuple_action: Whether the *action* space is composite.
        tuple_obs:    Whether the *observation* space is composite.

    Returns:
        A tuple `(cont_action, cont_obs)` of booleans.
    """

    def check_continuous(space: Space, tuple_flag: bool) -> bool:
        if isinstance(space, Box):
            return True
        elif isinstance(space, (Tuple, Dict)):
            if tuple_flag:
                for sub_space in space.spaces.values() if isinstance(space, Dict) else space.spaces:
                    if not check_continuous(sub_space, True):
                        return False
                return True
            else:
                return False
        else:
            return False
    cont_action = check_continuous(env.action_space, tuple_action)
    cont_obs = check_continuous(env.observation_space, tuple_obs)
    return (cont_action, cont_obs)

import numpy as np
def env_stats(actions: list, observations: list) -> dict:
    """Compute statistics for collections of actions and observations.

    The function inspects *actions* and *observations* and returns a dictionary
    containing information about dimensionality, data type (discrete or
    continuous) and the unique values appearing in every dimension.

    Parameters
    ----------
    actions : list
        A list with the actions that were taken.  Each element is either a
        scalar or an iterable of scalars (for multi-dimensional spaces).
    observations : list
        A list with the corresponding observations.  Same structural
        requirements as *actions*.

    Returns
    -------
    dict
        A dictionary with the keys described in the task description.
    """
    tuple_actions = False
    tuple_observations = False
    multidim_actions = False
    multidim_observations = False
    continuous_actions = False
    continuous_observations = False
    n_actions_per_dim = []
    n_obs_per_dim = []
    action_dim = 0
    obs_dim = 0
    action_ids = []
    obs_ids = []
    for action in actions:
        if isinstance(action, (tuple, list, np.ndarray)):
            tuple_actions = True
            multidim_actions = True
            action_dim = len(action)
            if not n_actions_per_dim:
                n_actions_per_dim = [0] * action_dim
            if isinstance(action, (tuple, list)):
                action = np.array(action)
            for i in range(action_dim):
                if isinstance(action[i], float) and (not action[i].is_integer()):
                    continuous_actions = True
                if action[i] not in action_ids[i]:
                    action_ids[i].append(action[i])
    if not multidim_actions and tuple_actions:
        multidim_actions = False
    if not action_ids:
        action_ids = [[]]
        action_dim = 0
    for observation in observations:
        if isinstance(observation, (tuple, list, np.ndarray)):
            tuple_observations = True
            multidim_observations = True
            obs_dim = len(observation)
            if not n_obs_per_dim:
                n_obs_per_dim = [0] * obs_dim
            if isinstance(observation, (tuple, list)):
                observation = np.array(observation)
            for i in range(obs_dim):
                if isinstance(observation[i], float) and (not observation[i].is_integer()):
                    continuous_observations = True
                if observation[i] not in obs_ids[i]:
                    obs_ids[i].append(observation[i])
        else:
            if isinstance(observation, float) and (not observation.is_integer()):
                continuous_observations = True
            if observation not in obs_ids[0]:
                obs_ids[0].append(observation)
    if not multidim_observations and tuple_observations:
        multidim_observations = False
    if not obs_ids:
        obs_ids = [[]]
        obs_dim = 0
    for i in range(action_dim):
        n_actions_per_dim[i] = len(set(action_ids[i]))
    for i in range(obs_dim):
        n_obs_per_dim[i] = len(set(obs_ids[i]))
    return {'tuple_actions': tuple_actions, 'tuple_observations': tuple_observations, 'multidim_actions': multidim_actions, 'multidim_observations': multidim_observations, 'continuous_actions': continuous_actions, 'continuous_observations': continuous_observations, 'n_actions_per_dim': n_actions_per_dim, 'action_dim': action_dim, 'n_obs_per_dim': n_obs_per_dim, 'obs_dim': obs_dim, 'action_ids': action_ids, 'obs_ids': obs_ids}

