import itertools
def apriori_frequent_itemsets(transactions: list[list[int]], min_sup: float) -> list[tuple]:
    """Find frequent itemsets with the Apriori algorithm.

    Parameters
    ----------
    transactions : list[list[Hashable]]
        List of transactions; each transaction is itself a list of items.
    min_sup : float
        Minimum support threshold expressed as a fraction (>0 and ≤1).

    Returns
    -------
    list[tuple]
        Frequent itemsets ordered by length and then lexicographically.
    """
    n_transactions = len(transactions)
    frequent_itemsets = []
    item_counts = {}
    for transaction in transactions:
        for item in transaction:
            item_counts[item] = item_counts.get(item, 0) + 1
    frequent_1_itemsets = {item for (item, count) in item_counts.items() if count / n_transactions >= min_sup}
    frequent_itemsets.extend([(item,) for item in sorted(frequent_1_itemsets)])
    k = 2
    while True:
        candidate_itemsets = set()
        for itemset1 in frequent_itemsets[-1]:
            for itemset2 in frequent_itemsets[-1]:
                if len(itemset1) == k - 1 and len(itemset2) == k - 1:
                    union_set = set(itemset1) | set(itemset2)
                    if len(union_set) == k:
                        candidate_itemsets.add(tuple(sorted(union_set)))
        if not candidate_itemsets:
            break
        pruned_candidates = set()
        for candidate in candidate_itemsets:
            all_subsets_frequent = True
            for i in range(1, k):
                for subset in itertools.combinations(candidate, i):
                    if tuple(sorted(subset)) not in [itemset for length in range(1, k) for itemset in frequent_itemsets[length - 1] if len(itemset) == length]:
                        all_subsets_frequent = False
                        break
                if not all_subsets_frequent:
                    break
            if all_subsets_frequent:
                pruned_candidates.add(candidate)
        itemset_counts = {}
        for transaction in transactions:
            for candidate in pruned_candidates:
                if set(candidate).issubset(set(transaction)):
                    itemset_counts[candidate] = itemset_counts.get(candidate, 0) + 1
        frequent_k_itemsets = {itemset for (itemset, count) in itemset_counts.items() if count / n_transactions >= min_sup}
        if not frequent_k_itemsets:
            break
        frequent_itemsets.extend([itemset for itemset in sorted(frequent_k_itemsets)])
        k += 1
    return frequent_itemsets

