import numpy as np
def dbscan(data: np.ndarray, eps: float, min_samples: int) -> list[int]:
    """Cluster *data* with the DBSCAN algorithm.

    Parameters
    ----------
    data : np.ndarray
        Two-dimensional array where each row is a sample and columns are
        features.
    eps : float
        Maximum radius of the neighborhood to be considered reachable.
    min_samples : int
        Minimum number of samples (including the point itself) required to form
        a dense region.

    Returns
    -------
    list[int]
        Cluster labels for each sample. Noise points are labeled ‑1 and cluster
        indices start at 0 and increase sequentially in discovery order.
    """
    if data.size == 0:
        return []
    n_samples = data.shape[0]
    labels = np.full(n_samples, -1, dtype=int)
    cluster_id = 0

    def euclidean_distance(point1, point2):
        return np.sqrt(np.sum((point1 - point2) ** 2))

    def region_query(point_index):
        neighbors = []
        for i in range(n_samples):
            if euclidean_distance(data[point_index], data[i]) <= eps:
                neighbors.append(i)
        return neighbors
    for i in range(n_samples):
        if labels[i] != -1:
            continue
        neighbors = region_query(i)
        if len(neighbors) < min_samples:
            labels[i] = -1
        else:
            labels[i] = cluster_id
            seed_set = neighbors
            while seed_set:
                j = seed_set.pop(0)
                if labels[j] == -1:
                    labels[j] = cluster_id
                elif labels[j] == -2:
                    labels[j] = cluster_id
                else:
                    continue
                new_neighbors = region_query(j)
                for neighbor in new_neighbors:
                    if labels[neighbor] == -1:
                        labels[neighbor] = -2
                        seed_set.append(neighbor)
                    elif labels[neighbor] == -2:
                        labels[neighbor] = cluster_id
            cluster_id += 1
    for i in range(n_samples):
        if labels[i] == -2:
            for j in range(n_samples):
                if labels[j] != -1 and labels[j] != -2:
                    labels[i] = labels[j]
                    break
    return labels.tolist()

