import itertools
def fp_growth(transactions, min_sup):
    """Mine all frequent item-sets using the FP-Growth algorithm.

    Parameters
    ----------
    transactions : Iterable[Iterable[Hashable]]
        A collection of transactions.  Each transaction is an iterable containing hashable items.
    min_sup : int
        Minimum number of occurrences an item-set must have to be considered frequent.

    Returns
    -------
    list[tuple]
        All frequent item-sets sorted 1) by length, 2) lexicographically.  Every
        tuple itself is sorted lexicographically.
    """
    item_counts = Counter((item for transaction in transactions for item in transaction))
    frequent_items = {item for (item, count) in item_counts.items() if count >= min_sup}
    if not frequent_items:
        return []

    def build_fp_tree(transactions, frequent_items):
        header_table = defaultdict(list)
        tree = {}
        for transaction in transactions:
            sorted_items = sorted([item for item in transaction if item in frequent_items], key=lambda item: item_counts[item], reverse=True)
            current_node = tree
            for item in sorted_items:
                if item not in current_node:
                    current_node[item] = {'count': 1, 'next': None, 'parent': current_node}
                    header_table[item].append(current_node[item])
                else:
                    current_node[item]['count'] += 1
                current_node = current_node[item]
        return (tree, header_table)
    (tree, header_table) = build_fp_tree(transactions, frequent_items)

    def mine_tree(tree, header_table, min_sup, current_item_set):
        frequent_itemsets = []
        if not header_table:
            return frequent_itemsets
        item = sorted(header_table.keys())[0]
        conditional_pattern_base = defaultdict(list)
        for node in header_table[item]:
            parent = node['parent']
            path = []
            while parent is not None:
                path.append(parent)
                parent = parent['parent']
            path.reverse()
            for node in path:
                if node is not tree:
                    conditional_pattern_base[node[list(node.keys())[0]]].append(node)
        (conditional_tree, conditional_header_table) = build_fp_tree([[item for item in transaction if item in conditional_pattern_base]], min_sup)
        if not conditional_tree:
            frequent_itemsets.append(tuple(sorted(current_item_set + [item])))
            return frequent_itemsets
        if len(conditional_header_table) == 1 and list(conditional_header_table.keys())[0] == list(tree.keys())[0]:
            path = []
            current = tree
            while current is not None:
                path.append(current)
                current = current.get(list(current.keys())[0], {}).get('parent')
            path.reverse()
            for i in range(1, len(path) + 1):
                for subset in itertools.combinations(path, i):
                    items = tuple(sorted([node[list(node.keys())[0]] for node in subset]))
                    if len(items) > 0:
                        frequent_itemsets.append(tuple(sorted(current_item_set + list(items))))
            return frequent_itemsets
        for item in conditional_header_table:
            frequent_itemsets.extend(mine_tree(conditional_tree, {item: conditional_header_table[item]}, min_sup, current_item_set + [item]))
        frequent_itemsets.append(tuple(sorted(current_item_set + [item])))
        return frequent_itemsets
    frequent_itemsets = []
    for item in frequent_items:
        frequent_itemsets.append((item,))
    frequent_itemsets.extend(mine_tree(tree, header_table, min_sup, []))
    unique_itemsets = set(frequent_itemsets)
    sorted_itemsets = sorted(list(unique_itemsets), key=lambda x: (len(x), x))
    return sorted_itemsets

