from typing import List, Dict, Tuple, Hashable

def find_frequent_itemsets(transactions: List[List[Hashable]], minsup: float) -> Dict[Tuple[Hashable, ...], int]:
    """Return all frequent itemsets in **transactions**.

    Args:
        transactions: List where each element represents a transaction and is
                       itself a list of items (hashable Python objects).
        minsup:       Minimum support given as a fraction between 0 and 1.

    Returns:
        A dictionary that maps every frequent itemset (stored as a tuple of
        sorted items) to its absolute support count.  If no itemset reaches
        the threshold the function must return an empty dictionary.
    """
    N = len(transactions)
    threshold = math.ceil(minsup * N)
    frequent_itemsets = {}

    # Find frequent 1-itemsets
    item_counts = Counter()
    for transaction in transactions:
        item_counts.update(set(transaction))  # Use set to avoid counting duplicates within a transaction

    for item, count in item_counts.items():
        if count >= threshold:
            frequent_itemsets[(item,)] = count

    # Find frequent k-itemsets for k > 1
    k = 2
    while True:
        candidate_itemsets = set()
        for itemset1 in frequent_itemsets:
            if len(itemset1) == k - 1:
                for itemset2 in frequent_itemsets:
                    if len(itemset2) == k - 1:
                        union = tuple(sorted(set(itemset1) | set(itemset2)))
                        if len(union) == k:
                            candidate_itemsets.add(union)

        if not candidate_itemsets:
            break

        itemset_counts = Counter()
        for transaction in transactions:
            for itemset in candidate_itemsets:
                if set(itemset).issubset(transaction):
                    itemset_counts[itemset] += 1

        for itemset, count in itemset_counts.items():
            if count >= threshold:
                frequent_itemsets[itemset] = count

        k += 1

    return frequent_itemsets
