from collections import Counter, defaultdict
def fp_growth(transactions: list[list[str]], min_support: int) -> list[list[str]]:
    """Discover every frequent item-set in *transactions* with FP-Growth.

    A *transaction* is represented by a list of items (strings).  `min_support`
    is the minimum number of transactions an item-set has to appear in.  The
    function returns **all** frequent item-sets where
        support(itemset) >= min_support.

    The result must be deterministic:
      • Inside each item-set the items have to be sorted alphabetically.
      • The outer list has to be sorted by `(len(itemset), itemset)`.
    If *transactions* is empty or no item-set meets the threshold return an
    empty list.
    """
    if not transactions:
        return []

    # 1. Count support of single items
    item_counts = Counter()
    for transaction in transactions:
        item_counts.update(transaction)

    frequent_items = {item for item, count in item_counts.items() if count >= min_support}
    if not frequent_items:
        return []

    # 2. Build FP-tree
    def build_fp_tree(transactions, frequent_items):
        tree = {}

        def insert_transaction(transaction):
            current_node = tree
            for item in transaction:
                if item in frequent_items:
                    if item not in current_node:
                        current_node[item] = {'count': 0, 'children': {}}
                    current_node[item]['count'] += 1
                    current_node = current_node[item]['children']


        for transaction in transactions:
            filtered_transaction = sorted([item for item in transaction if item in frequent_items])
            insert_transaction(filtered_transaction)
        return tree

    fp_tree = build_fp_tree(transactions, frequent_items)

    # 3. Recursive mining
    frequent_itemsets = []

    def mine_tree(node, current_itemset):
        if not node:
            return

        for item, data in node.items():
            new_itemset = sorted(current_itemset + [item])
            count = data['count']

            if count >= min_support:
                frequent_itemsets.append(new_itemset)

            # Create conditional database
            conditional_db = []
            for transaction in transactions:
                filtered_transaction = sorted([i for i in transaction if i in frequent_items])
                if new_itemset[:len(new_itemset)-1] == sorted(filtered_transaction[:len(filtered_transaction)]):
                    conditional_db.append([i for i in filtered_transaction[len(new_itemset)-1:]])

            # Recursive call
            mine_tree(data['children'], new_itemset)

    mine_tree(fp_tree, [])

    # Sort the results
    frequent_itemsets.sort(key=lambda x: (len(x), x))

    return frequent_itemsets
