import numpy as np
def conv2D(X: "np.ndarray", W: "np.ndarray", stride: int, pad, dilation: int = 0):
    """Performs a 2-D convolution (cross-correlation).

    Args:
        X: NumPy array of shape ``(n_ex, in_rows, in_cols, in_ch)`` representing the input batch.
        W: NumPy array of shape ``(kernel_rows, kernel_cols, in_ch, out_ch)`` containing the kernels.
        stride: Stride of the convolution.
        pad: Padding specification – integer, tuple or the string ``'same'``.
        dilation: Optional dilation factor. ``0`` corresponds to a normal convolution.

    Returns:
        The convolution result **as a Python list** obtained through ``numpy.ndarray.tolist()``.
    """
    n_ex, in_rows, in_cols, in_ch = X.shape
    kernel_rows, kernel_cols, _, out_ch = W.shape

    if isinstance(pad, int):
        pad_rows = pad
        pad_cols = pad
    elif isinstance(pad, tuple):
        if len(pad) == 2:
            pad_rows = pad[0]
            pad_cols = pad[1]
        elif len(pad) == 4:
            pad_rows = (pad[0], pad[1])
            pad_cols = (pad[2], pad[3])
        else:
            raise ValueError("Invalid padding tuple length")
    elif pad == 'same':
        pad_rows = (kernel_rows - 1) // 2 * dilation
        pad_cols = (kernel_cols - 1) // 2 * dilation
    else:
        raise ValueError("Invalid padding specification")

    if isinstance(pad_rows, int):
        padded_rows = in_rows + 2 * pad_rows
        padded_cols = in_cols + 2 * pad_cols
    else:
        padded_rows = in_rows + pad_rows[0] + pad_rows[1]
        padded_cols = in_cols + pad_cols[0] + pad_cols[1]

    padded_X = np.pad(X, ((0, 0), (pad_rows[0], pad_rows[1]), (pad_cols[0], pad_cols[1]), (0, 0)), mode='constant')

    out_rows = (padded_rows - kernel_rows * (dilation + 1) + 1) // stride
    out_cols = (padded_cols - kernel_cols * (dilation + 1) + 1) // stride

    im2col = np.zeros((n_ex, padded_rows * padded_cols, kernel_rows * kernel_cols * in_ch))
    for n in range(n_ex):
        for i in range(padded_rows):
            for j in range(padded_cols):
                col_idx = i * padded_cols + j
                for k in range(kernel_rows):
                    for l in range(kernel_cols):
                        row_idx = i + k * (dilation + 1)
                        col_idx_kernel = j + l * (dilation + 1)
                        if row_idx < padded_rows and col_idx_kernel < padded_cols:
                            channel_idx = 0
                            for c in range(in_ch):
                                im2col[n, col_idx, channel_idx + k * kernel_cols * in_ch + l * in_ch] = padded_X[n, row_idx, col_idx_kernel, c]
                                channel_idx += 1

    W_reshaped = W.reshape(kernel_rows * kernel_cols * in_ch, out_ch)
    out = np.dot(im2col, W_reshaped)
    out = out.reshape(n_ex, out_rows, out_cols, out_ch)

    return out.tolist()
