# ------------------  Minimal imitation of Gym spaces (do not remove)  ------------------
class Space:  # abstract base class
    pass

class Box(Space):
    def __init__(self, low: float, high: float, shape: PyTuple[int, ...]):
        self.low = low
        self.high = high
        self.shape = shape

class Discrete(Space):
    def __init__(self, n: int):
        self.n = n

class Tuple(Space):
    def __init__(self, spaces: Iterable[Space]):
        self.spaces = tuple(spaces)

class Dict(Space):
    def __init__(self, spaces: Dict[str, Space]):
        self.spaces = dict(spaces)

class Env:
    """Tiny environment that only stores two spaces."""
    def __init__(self, action_space: Space, observation_space: Space):
        self.action_space = action_space
        self.observation_space = observation_space

# ----------------------------  Complete this function  ----------------------------
def is_continuous(env: Env, tuple_action: bool, tuple_obs: bool):
    """Determine whether the given environment's spaces are continuous.

    A space is *continuous* if it is an instance of `Box`. For composite spaces
    (`Tuple` or `Dict`) the space is continuous only if **all** its sub-spaces
    are `Box`.

    Args:
        env:          Environment exposing `action_space` and `observation_space`.
        tuple_action: Whether the *action* space is composite.
        tuple_obs:    Whether the *observation* space is composite.

    Returns:
        A tuple `(cont_action, cont_obs)` of booleans.
    """
    def is_box(space: Space) -> bool:
        return isinstance(space, Box)

    def is_all_boxes(spaces: Iterable[Space]) -> bool:
        return all(is_box(s) for s in spaces)

    if tuple_action:
        if isinstance(env.action_space, Tuple):
            cont_action = is_all_boxes(env.action_space.spaces)
        elif isinstance(env.action_space, Dict):
            cont_action = is_all_boxes(env.action_space.spaces.values())
        else:
            cont_action = False  # Should not happen given the input
    else:
        cont_action = is_box(env.action_space)

    if tuple_obs:
        if isinstance(env.observation_space, Tuple):
            cont_obs = is_all_boxes(env.observation_space.spaces)
        elif isinstance(env.observation_space, Dict):
            cont_obs = is_all_boxes(env.observation_space.spaces.values())
        else:
            cont_obs = False  # Should not happen given the input
    else:
        cont_obs = is_box(env.observation_space)

    return (cont_action, cont_obs)
