import numpy as np
def dbscan(data: np.ndarray, eps: float, min_samples: int) -> list[int]:
    """Cluster *data* with the DBSCAN algorithm.

    Parameters
    ----------
    data : np.ndarray
        Two-dimensional array where each row is a sample and columns are
        features.
    eps : float
        Maximum radius of the neighborhood to be considered reachable.
    min_samples : int
        Minimum number of samples (including the point itself) required to form
        a dense region.

    Returns
    -------
    list[int]
        Cluster labels for each sample. Noise points are labeled ‑1 and cluster
        indices start at 0 and increase sequentially in discovery order.
    """
    if data.size == 0:
        return []

    n_samples = data.shape[0]
    labels = [-1] * n_samples
    cluster_id = 0

    def euclidean_distance(point1, point2):
        return np.sqrt(np.sum((point1 - point2) ** 2))

    def get_neighbors(sample_index):
        neighbors = []
        for i in range(n_samples):
            if euclidean_distance(data[sample_index], data[i]) <= eps:
                neighbors.append(i)
        return neighbors

    def is_core_point(sample_index):
        neighbors = get_neighbors(sample_index)
        return len(neighbors) >= min_samples

    def expand_cluster(sample_index, cluster_id):
        neighbors = get_neighbors(sample_index)

        for neighbor_index in neighbors:
            if labels[neighbor_index] == -1:
                labels[neighbor_index] = cluster_id
            elif labels[neighbor_index] != cluster_id:
                pass # already assigned to another cluster


    for i in range(n_samples):
        if labels[i] == -1 and is_core_point(i):
            expand_cluster(i, cluster_id)
            cluster_id += 1

    # Assign border points to the nearest core point
    for i in range(n_samples):
        if labels[i] == -1:
            neighbors = get_neighbors(i)
            for neighbor_index in neighbors:
                if labels[neighbor_index] != -1:
                    labels[i] = labels[neighbor_index]
                    break

    return labels
