import numpy as np
import math

def detect_outliers(data: np.ndarray, n_trees: int, sample_size: int, epsilon: float) -> list[int]:
    """Tiny Isolation Forest implementation.

    Args:
        data: 2-D NumPy array where each row is an observation and each column a feature.
        n_trees: Number of random isolation trees that will be grown.
        sample_size: Number of samples drawn (without replacement) to grow every tree.
        epsilon: Fraction (0 ≤ ε < 1) of observations that are considered outliers.

    Returns:
        A list with the zero-based indices of the detected outliers, sorted increasingly.
    """
    np.random.seed(42)
    n_samples = data.shape[0]
    if epsilon == 0:
        return []

    path_lengths = np.zeros(n_samples)

    for _ in range(n_trees):
        # Subsample the data
        if sample_size >= n_samples:
            subsample = data
        else:
            indices = np.random.choice(n_samples, sample_size, replace=False)
            subsample = data[indices]

        # Build the tree
        tree = build_tree(subsample)

        # Calculate path lengths for all samples
        for i in range(n_samples):
            path_lengths[i] += path_length(data[i], tree)

    # Calculate anomaly scores
    phi = 2 * np.log(n_samples - 1) - 2 * (n_samples - 1) / n_samples
    scores = 2 ** (-path_lengths / phi)

    # Determine outliers
    threshold = np.percentile(scores, 100 * (1 - epsilon))
    outlier_indices = np.where(scores >= threshold)[0].tolist()
    outlier_indices.sort()

    return outlier_indices

def build_tree(data: np.ndarray):
    """Builds a random isolation tree."""
    if data.shape[0] <= 1:
        return {'leaf': True, 'n': data.shape[0]}

    height_limit = int(np.ceil(np.log2(data.shape[0])))

    def recursive_split(data, depth):
        if depth == height_limit or data.shape[0] <= 1:
            return {'leaf': True, 'n': data.shape[0]}

        # Choose a random feature and threshold
        feature_index = np.random.randint(data.shape[1])
        min_val = np.min(data[:, feature_index])
        max_val = np.max(data[:, feature_index])
        threshold = np.random.uniform(min_val, max_val)

        # Split the data
        left_data = data[data[:, feature_index] <= threshold]
        right_data = data[data[:, feature_index] > threshold]

        # Recursively split the subtrees
        left_subtree = recursive_split(left_data, depth + 1)
        right_subtree = recursive_split(right_data, depth + 1)

        return {'leaf': False, 'feature': feature_index, 'threshold': threshold,
                'left': left_subtree, 'right': right_subtree}

    return recursive_split(data, 0)

def path_length(x: np.ndarray, tree):
    """Calculates the path length of a sample in a tree."""
    if tree['leaf']:
        return c(tree['n'])

    if x[tree['feature']] <= tree['threshold']:
        return 1 + path_length(x, tree['left'])
    else:
        return 1 + path_length(x, tree['right'])

def c(n: int):
    """Approximation of the expected path length."""
    if n == 1:
        return 0
    else:
        return 2 * np.log(n - 1) + 0.5772156649 - 2 * (n - 1) / n
