import math
from collections import defaultdict
import numpy as np

def maxent_predict(
    X_train: np.ndarray,
    y_train: np.ndarray,
    X_test: np.ndarray,
    epsilon: float = 1e-3,
    n_iter: int = 100,
) -> list[int]:
    """
    Implements a Maximum Entropy (MaxEnt) classifier using the Generalized Iterative Scaling (GIS) algorithm.

    Args:
        X_train (np.ndarray): Training samples.
        y_train (np.ndarray): Training labels.
        X_test (np.ndarray): Samples to classify.
        epsilon (float): Convergence threshold for GIS.
        n_iter (int): Maximum number of iterations for GIS.

    Returns:
        list[int]: Predicted labels for the test samples.
    """

    n_samples_train = X_train.shape[0]
    n_samples_test = X_test.shape[0]
    n_features = X_train.shape[1]
    labels = np.unique(y_train)
    n_labels = len(labels)

    # Build empirical distributions
    counts = defaultdict(lambda: defaultdict(int))
    for i in range(n_samples_train):
        for j in range(n_features):
            counts[(j, X_train[i, j])][y_train[i]] += 1

    # Initialize weights
    weights = defaultdict(float)
    for j in range(n_features):
        for val in np.unique(X_train[:, j]):
            for y in labels:
                weights[(j, val, y)] = 0.0

    # GIS Iterations
    for _ in range(n_iter):
        updates = []
        for j in range(n_features):
            for val in np.unique(X_train[:, j]):
                for y in labels:
                    # Calculate conditional probability
                    Z_x = 0.0
                    for y_prime in labels:
                        score = 0.0
                        for k in range(n_features):
                            for v in np.unique(X_train[:, k]):
                                if (k, v, y_prime) in weights:
                                    score += weights[(k, v, y_prime)] * (1 if X_train[0, k] == v else 0)
                        Z_x += math.exp(score)

                    P_y_given_x = math.exp(weights[(j, val, y)]) / Z_x

                    # Calculate empirical probability
                    empirical_prob = counts[(j, val)][y] / n_samples_train

                    # Update weight
                    update = 0.0
                    if empirical_prob > P_y_given_x:
                        update = 1.0
                    else:
                        update = 0.0

                    weights[(j, val, y)] += update
                    updates.append(abs(update))

        if len(updates) > 0 and max(updates) < epsilon:
            break

    # Prediction
    predictions = []
    for i in range(n_samples_test):
        best_label = None
        max_prob = -1.0
        for y in labels:
            score = 0.0
            for j in range(n_features):
                for val in np.unique(X_train[:, j]):
                    if (j, val, y) in weights:
                        score += weights[(j, val, y)] * (1 if X_test[i, j] == val else 0)

            Z_x = 0.0
            for y_prime in labels:
                score_prime = 0.0
                for k in range(n_features):
                    for v in np.unique(X_train[:, k]):
                        if (k, v, y_prime) in weights:
                            score_prime += weights[(k, v, y_prime)] * (1 if X_test[i, k] == v else 0)
                Z_x += math.exp(score_prime)

            prob = math.exp(score) / Z_x
            if prob > max_prob:
                max_prob = prob
                best_label = y
        predictions.append(best_label)

    return predictions
