def calculate_matrix_mean(matrix: list[list[float]], mode: str) -> list[float]:
    """
    Calculates the mean of a matrix either by row or by column, based on a given mode.

    Args:
        matrix (list[list[float]]): The input matrix (list of lists of floats).
        mode (str): The mode of calculation ('row' or 'column').

    Returns:
        list[float]: A list of means according to the specified mode.
    """
    if not matrix:
        return []
    if mode == 'row':
        means = []
        for row in matrix:
            if not row:
                means.append(0.0)
            else:
                means.append(sum(row) / len(row))
        return means
    elif mode == 'column':
        num_cols = len(matrix[0])
        means = []
        for j in range(num_cols):
            col_sum = 0
            for i in range(len(matrix)):
                col_sum += matrix[i][j]
            means.append(col_sum / len(matrix))
        return means
    else:
        raise ValueError("Invalid mode. Mode must be 'row' or 'column'.")
assert calculate_matrix_mean([[1, 2, 3], [4, 5, 6], [7, 8, 9]], 'column') == [4.0, 5.0, 6.0]
assert calculate_matrix_mean([[1, 2, 3], [4, 5, 6], [7, 8, 9]], 'row') == [2.0, 5.0, 8.0]

