def learn_decision_tree(examples: list[dict], attributes: list[str], target_attr: str) -> dict:
    """
    Implements the decision tree learning algorithm for classification.

    Args:
        examples: A list of examples, each a dictionary of attribute-value pairs.
        attributes: A list of attribute names.
        target_attr: The name of the target attribute.

    Returns:
        A nested dictionary representing the decision tree.
    """
    if not examples:
        return {}
    if len(attributes) == 0:
        class_counts = Counter([example[target_attr] for example in examples])
        most_common_class = class_counts.most_common(1)[0][0]
        return most_common_class
    best_attribute = None
    best_gain = -1
    for attribute in attributes:
        values = {}
        for example in examples:
            value = example[attribute]
            if value not in values:
                values[value] = []
            values[value].append(example[target_attr])
        entropy_before = calculate_entropy([example[target_attr] for example in examples])
        weighted_gain = 0
        for (value, labels) in values.items():
            subset = [example for example in examples if example[attribute] == value]
            entropy_after = calculate_entropy(labels)
            gain = entropy_before - len(subset) / len(examples) * entropy_after
            weighted_gain += gain
        if weighted_gain > best_gain:
            best_gain = weighted_gain
            best_attribute = attribute
    tree = {}
    if best_attribute:
        tree[best_attribute] = {}
        values = {}
        for example in examples:
            value = example[best_attribute]
            if value not in values:
                values[value] = []
            values[value].append(example[target_attr])
        for value in values:
            subset = [example for example in examples if example[best_attribute] == value]
            tree[best_attribute][value] = learn_decision_tree(subset, [attr for attr in attributes if attr != best_attribute], target_attr)
    else:
        class_counts = Counter([example[target_attr] for example in examples])
        most_common_class = class_counts.most_common(1)[0][0]
        tree = most_common_class
    return tree
def calculate_entropy(labels: list[any]) -> float:
    """
    Calculates the entropy of a list of labels.

    Args:
        labels: A list of labels.

    Returns:
        The entropy of the labels.
    """
    if not labels:
        return 0
    counts = Counter(labels)
    entropy = 0
    for count in counts.values():
        probability = count / len(labels)
        entropy -= probability * math.log2(probability)
    return entropy
