import numpy as np
def compute_tf_idf(corpus, query):
    """
    Computes the TF-IDF scores for a query against a given corpus of documents.

    Args:
        corpus: A list of documents, where each document is a list of words.
        query: A list of words for which you want to compute the TF-IDF scores.

    Returns:
        A list of lists containing the TF-IDF scores for the query words in each document,
        rounded to five decimal places.
    """
    if not corpus:
        return []
    tf = []
    for document in corpus:
        term_counts = {}
        for term in document:
            term_counts[term] = term_counts.get(term, 0) + 1
        doc_length = len(document)
        tf_doc = []
        for term in query:
            if term in term_counts:
                tf_doc.append(term_counts[term] / doc_length)
            else:
                tf_doc.append(0.0)
        tf.append(tf_doc)
    df = {}
    N = len(corpus)
    for document in corpus:
        unique_terms = set(document)
        for term in unique_terms:
            df[term] = df.get(term, 0) + 1
    idf = {}
    for term in df:
        idf[term] = np.log(N / df[term])
    tf_idf = []
    for tf_doc in tf:
        tfidf_doc = []
        for (i, term) in enumerate(query):
            tfidf_doc.append(tf_doc[i] * idf.get(term, 0.0))
        tf_idf.append(tfidf_doc)
    return np.array(tf_idf).tolist()
