def fp_growth(transactions: list[list[str]], min_support: int) -> list[list[str]]:
    """Discover every frequent item-set in *transactions* with FP-Growth.

    A *transaction* is represented by a list of items (strings).  `min_support`
    is the minimum number of transactions an item-set has to appear in.  The
    function returns **all** frequent item-sets where
        support(itemset) >= min_support.

    The result must be deterministic:
      • Inside each item-set the items have to be sorted alphabetically.
      • The outer list has to be sorted by `(len(itemset), itemset)` so that calling `sort` with that key does **not** change the order.
    If *transactions* is empty or no item-set meets the threshold return an
    empty list.
    """
    if not transactions:
        return []
    item_counts = Counter([item for transaction in transactions for item in transaction])
    infrequent_items = [item for (item, count) in item_counts.items() if count < min_support]
    filtered_transactions = [[item for item in transaction if item not in infrequent_items] for transaction in transactions]
    fp_tree = FPTree(filtered_transactions)
    frequent_itemsets = fp_tree.generate_frequent_itemsets()
    frequent_itemsets.sort(key=lambda x: (len(x), x))
    return frequent_itemsets
class FPTree:

    def __init__(self, transactions):
        self.tree = {}
        self.transactions = transactions

    def build_tree(self):
        for transaction in self.transactions:
            self.insert_transaction(transaction)

    def insert_transaction(self, transaction):
        current_node = self.tree
        for item in transaction:
            if item not in current_node:
                current_node[item] = {}
            current_node = current_node[item]
        current_node['count'] = current_node.get('count', 0) + 1

    def generate_frequent_itemsets(self):
        frequent_itemsets = set()
        self._mine_frequent_itemsets(self.tree, frequent_itemsets)
        return sorted(list(frequent_itemsets), key=lambda x: (len(x), x))

    def _mine_frequent_itemsets(self, node, frequent_itemsets):
        if 'count' in node:
            if node['count'] >= 1:
                frequent_itemsets.add(tuple(sorted(node.keys() - {'count'})))
        for (item, next_node) in node.items():
            if item != 'count':
                self._mine_frequent_itemsets(next_node, frequent_itemsets)

