TIME_STEPS = 20
def string_to_int(text: str, time_steps: int, vocabulary: dict[str, int]) -> list[int]:
    """Encodes a string into a fixed-length list of integer token ids."""
    encoded = []
    for char in text:
        if char in vocabulary:
            encoded.append(vocabulary[char])
        else:
            encoded.append(0)
    if len(encoded) < time_steps:
        encoded.extend([0] * (time_steps - len(encoded)))
    elif len(encoded) > time_steps:
        encoded = encoded[:time_steps]
    return np.array(encoded)
def int_to_string(indices, inverse_vocab: dict[int, str]) -> str:
    """Converts a list/array of integer ids back to text, omitting padding tokens."""
    result = ''
    for i in indices:
        if i in inverse_vocab:
            result += inverse_vocab[i]
    return result
def run_example(model, input_vocabulary: dict[str, int], inv_output_vocabulary: dict[int, str], text: str) -> str:
    """Runs the entire process: encoding, prediction, and decoding."""
    encoded_text = string_to_int(text, TIME_STEPS, input_vocabulary)
    prediction = model.predict(encoded_text)
    predicted_id = np.argmax(prediction)
    decoded_text = int_to_string(predicted_id, inv_output_vocabulary)
    return decoded_text

