        action_ids = None
        action_dim = len(env.action_space.spaces)
    else:
        action_dim = 1
        action_ids = None
    if cont_action:
        n_actions_per_dim = [np.inf] * action_dim
        action_ids = None
    else:
        if md_action:
            for dim in env.action_space.spaces:
                if hasattr(dim, 'n'):
                    n_actions_per_dim.append(dim.n)
                elif hasattr(dim, 'shape'):
                    n_actions_per_dim.append(np.inf)
