def apriori_frequent_itemsets(transactions: list[list[int]], min_sup: float) -> list[tuple]:
    """Find frequent itemsets with the Apriori algorithm.

    Parameters
    ----------
    transactions : list[list[Hashable]]
        List of transactions; each transaction is itself a list of items.
    min_sup : float
        Minimum support threshold expressed as a fraction (>0 and ≤1).

    Returns
    -------
    list[tuple]
        Frequent itemsets ordered by length and then lexicographically.
    """
    item_counts = {}
    for transaction in transactions:
        for item in transaction:
            item_counts[item] = item_counts.get(item, 0) + 1
    num_transactions = len(transactions)
    frequent_items = {item: count for (item, count) in item_counts.items() if count / num_transactions >= min_sup}
    frequent_itemsets = [frozenset([item]) for item in frequent_items]
    k = 2
    while True:
        candidate_itemsets = set()
        for itemset1 in frequent_itemsets:
            for itemset2 in frequent_itemsets:
                if len(itemset1.union(itemset2)) == k:
                    candidate_itemsets.add(itemset1.union(itemset2))
        frequent_candidate_itemsets = []
        for candidate in candidate_itemsets:
            is_frequent = True
            for subset in candidate:
                if subset not in frequent_items:
                    is_frequent = False
                    break
            if is_frequent:
                frequent_candidate_itemsets.append(candidate)
        if not frequent_candidate_itemsets:
            break
        frequent_itemsets.extend([frozenset(itemset) for itemset in frequent_candidate_itemsets])
        k += 1
    frequent_itemsets_list = sorted([tuple(sorted(itemset)) for itemset in frequent_itemsets], key=lambda x: (len(x), x))
    return frequent_itemsets_list

import numpy as np
def rbf_kernel(X: list[list[int | float]], Y: list[list[int | float]] | None=None, sigma: float | list[float] | None=None) -> list[list[float]]:
    """Compute the Radial Basis Function (RBF) kernel matrix.

    The function should follow the specifications given in the task
    description. It must return -1 on invalid input, otherwise a nested list
    containing the kernel matrix rounded to four decimal places.
    """
    X = np.array(X)
    if Y is not None:
        Y = np.array(Y)
    else:
        Y = np.ones((X.shape[0], X.shape[1]))
    if X.shape[1] != Y.shape[1]:
        return -1
    if sigma is not None:
        if not isinstance(sigma, (float, list)):
            return -1
        if isinstance(sigma, float):
            sigma = [sigma] * X.shape[1]
        if len(sigma) != X.shape[1]:
            return -1
        if any((s <= 0 for s in sigma)):
            return -1
    else:
        sigma = np.sqrt(X.shape[1] / 2)
    kernel_matrix = np.zeros((X.shape[0], Y.shape[0]))
    for i in range(X.shape[0]):
        for j in range(Y.shape[0]):
            distance = np.linalg.norm(X[i] - Y[j])
            kernel_matrix[i, j] = np.exp(-0.5 * distance ** 2 / sigma ** 2)
    return [list(np.round(kernel_matrix, 4))]

