def regression_tree_predict(X_train: list[float | int], y_train: list[float | int], X_test: list[float | int], max_depth: int=3) -> list[float]:
    """Fit a univariate regression tree of depth *max_depth* and predict.

    Parameters
    ----------
    X_train : list[float | int]
        1-D feature values for the training set.
    y_train : list[float | int]
        Continuous target values for the training set.
    X_test : list[float | int]
        1-D feature values for which predictions are required.
    max_depth : int, default 3
        Maximum depth of the binary tree (root has depth 0).

    Returns
    -------
    list[float]
        Predictions for every element in *X_test*.
    """
    X_train = np.array(X_train)
    y_train = np.array(y_train)
    X_test = np.array(X_test)

    def build_tree(X, y, depth):
        if depth == max_depth or len(y) < 2:
            return {'leaf': True, 'value': np.mean(y)}
        best_split = None
        min_sse = float('inf')
        for i in range(len(X.flatten())):
            threshold = X[i]
            left_indices = X < threshold
            right_indices = X >= threshold
            left_y = y[left_indices]
            right_y = y[right_indices]
            if len(left_y) > 0 and len(right_y) > 0:
                sse_left = np.sum((left_y - np.mean(left_y)) ** 2)
                sse_right = np.sum((right_y - np.mean(right_y)) ** 2)
                sse = sse_left + sse_right
                if sse < min_sse:
                    min_sse = sse
                    best_split = {'threshold': threshold, 'left': left_indices, 'right': right_indices}
        if best_split is None:
            return {'leaf': True, 'value': np.mean(y)}
        left_child = build_tree(X[best_split['left']], y[best_split['left']], depth + 1)
        right_child = build_tree(X[best_split['right']], y[best_split['right']], depth + 1)
        return {'split': best_split, 'left': left_child, 'right': right_child}
    tree = build_tree(X_train, y_train, 0)

    def predict(tree, x):
        if 'leaf' in tree:
            return tree['value']
        else:
            threshold = tree['split']['threshold']
            left_indices = tree['split']['left']
            right_indices = tree['split']['right']
            if x < threshold:
                return predict(tree['left'], x)
            else:
                return predict(tree['right'], x)
    predictions = [predict(tree, x) for x in X_test]
    return predictions

