def fp_growth(transactions, min_sup):
    """Mine all frequent item-sets using the FP-Growth algorithm.

    Parameters
    ----------
    transactions : Iterable[Iterable[Hashable]]
        A collection of transactions.  Each transaction is an iterable containing hashable items.
    min_sup : int
        Minimum number of occurrences an item-set must have to be considered frequent.

    Returns
    -------
    list[tuple]
        All frequent item-sets sorted 1) by length, 2) lexicographically.  Every
        tuple itself is sorted lexicographically.
    """
    item_counts = Counter()
    for transaction in transactions:
        item_counts.update(transaction)
    support_threshold = len(transactions) * min_sup / len(transactions)
    frequent_items = [item for (item, count) in item_counts.items() if count >= support_threshold]
    frequent_items.sort()
    fp_tree = FPTree(frequent_items)
    for transaction in transactions:
        fp_tree.insert(transaction)
    frequent_itemsets = []

    def mine(node, itemset):
        if node.is_leaf():
            if len(itemset) > 1:
                frequent_itemsets.append(tuple(sorted(itemset)))
            return
        for item in node.children:
            child_node = node.children[item]
            mine(child_node, itemset + [item])
    mine(fp_tree.root, [])
    frequent_itemsets.sort(key=lambda x: (len(x), tuple(x)))
    return frequent_itemsets
class Node:

    def __init__(self, item=None):
        self.item = item
        self.children = {}
        self.prefix_count = 1
        self.is_leaf = True

    def add_child(self, item, node):
        self.children[item] = node
        self.is_leaf = False

    def count(self):
        if self.is_leaf:
            return 1
        else:
            return self.prefix_count
class FPTree:

    def __init__(self, frequent_items):
        self.frequent_items = frequent_items
        self.root = Node()
        self.headers = defaultdict(Node)
        for item in frequent_items:
            self.headers[item].add_child(item, self.root)

    def insert(self, transaction):
        current_node = self.root
        for item in transaction:
            if item not in current_node.children:
                current_node.add_child(item, Node())
                current_node = current_node.children[item]
            else:
                current_node = current_node.children[item]
            self.headers[item].add_child(item, current_node)
            current_node.prefix_count += 1

