def statistic_key_word(data: List[Tuple[Any, Iterable[str]]], cut_off: int | None=None) -> Tuple[List[Tuple[str, float]], Set[str]]:
    """Calculate how often each word appears across documents.

    The function returns a list with the document-frequency ratio of every word
    (sorted from highest to lowest) and a set containing only the *cut_off*
    most frequent words. When *cut_off* is ``None`` all words are placed in the
    set. Every ratio must be rounded to four decimal places.

    Args:
        data: Iterable of documents. Each document is represented by a tuple
              ``(label, words)`` where *label* is ignored and *words* is an
              iterable of strings.
        cut_off: Number of top words to include in the returned set, or
                  ``None`` to include every word.

    Returns:
        Tuple with (1) a list of (word, ratio) pairs and (2) a set of the most
        frequent words as described above.
    """
    word_counts = Counter()
    for (_, words) in data:
        word_counts.update(words)
    total_documents = len(data)
    if total_documents == 0:
        return ([], set())
    ratios = {word: count / total_documents for (word, count) in word_counts.items()}
    sorted_ratios = sorted(ratios.items(), key=lambda item: item[1], reverse=True)
    if cut_off is None:
        most_common_words = set((word for (word, _) in sorted_ratios))
    else:
        most_common_words = set((word for (word, _) in sorted_ratios[:cut_off]))
    if cut_off is None:
        return (sorted_ratios, most_common_words)
    else:
        return (sorted_ratios, most_common_words)

import numpy as np
def local_outlier_factor(data, k, epsilon=1.5):
    """Your task is to implement this function!"""
    n = len(data)
    if k >= n:
        raise ValueError('k must be less than n')
    distance_matrix = np.zeros((n, n))
    for i in range(n):
        for j in range(i + 1, n):
            distance_matrix[i, j] = np.linalg.norm(data[i] - data[j])
            distance_matrix[j, i] = distance_matrix[i, j]
    k_distances = []
    nearest_neighbors_indices = []
    for i in range(n):
        distances = distance_matrix[i, :n]
        k_distance = np.sort(distances)[k - 1]
        k_distances.append(k_distance)
        nearest_neighbors_indices.append(np.argsort(distances)[:k])
    reachability_distances = np.zeros((n, n))
    for i in range(n):
        for j in range(n):
            reachability_distances[i, j] = max(k_distances[j], distance_matrix[i, j])
    lrd_scores = np.zeros(n)
    for i in range(n):
        numerator = np.sum(np.array([lrd_scores[j] for j in nearest_neighbors_indices[i]]) / reachability_distances[i, nearest_neighbors_indices[i]])
        lrd_scores[i] = k / numerator
    lof_scores = np.zeros(n)
    for i in range(n):
        numerator = np.sum(lrd_scores[nearest_neighbors_indices[i]] / reachability_distances[i, nearest_neighbors_indices[i]])
        lof_scores[i] = numerator / k / lrd_scores[i]
    lof_scores = np.round(lof_scores, 4)
    outlier_indices = np.where(lof_scores > epsilon)[0].tolist()
    return (list(lof_scores), outlier_indices)
