import numpy as np
from sklearn.linear_model import Lasso
def lasso_regression(X: list[float] | 'np.ndarray', y: list[float] | 'np.ndarray', degree: int, reg_factor: float, n_iterations: int=1000, tol: float=1e-06) -> list[float]:
    """Train a univariate Lasso regression model."""
    if not isinstance(X, list):
        raise TypeError("Input 'X' must be a list.")
    if not all((isinstance(x, list) for x in X)):
        raise ValueError("All elements in 'X' must be lists.")
    if not isinstance(y, list):
        raise TypeError("Input 'y' must be a list.")
    if not all((isinstance(y, list) for y in y)):
        raise ValueError("All elements in 'y' must be lists.")
    if len(X[0]) == 1 and len(y) != 1:
        raise ValueError("'X' must have at least two dimensions.")
    if isinstance(X, str):
        X = np.array([list(map(float, line.split(','))) for line in open(X)])
    if isinstance(y, str):
        y = np.array([list(map(float, line.split(','))) for line in open(y)])
    design_matrix = np.ones((len(X), degree + 1))
    for i in range(len(design_matrix)):
        design_matrix[i] = X[:i + 1].reshape(-1, 1)
    y = np.array(y).reshape(-1, 1)
    alpha = np.linalg.inv(np.dot(design_matrix.T, design_matrix)) * reg_factor
    w = np.linalg.solve(alpha, y - np.dot(design_matrix, alpha))
    w = map(round, w)
    max_iter = min(n_iterations, len(w) - 1)
    last_change = None
    for _ in range(max_iter):
        last_change = np.abs(np.max(np.abs(w)))
        w += alpha @ (np.sign(last_change) * np.zeros_like(w))
        if np.linalg.norm(last_change) < tol:
            break
    return w.tolist()
