def adaboost_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, n_clf: int=5) -> list[int]:
    if n_clf < 1:
        n_clf = 1

    def calculate_weight(y_train):
        return np.sum(np.sign(y_train == 1)) / len(y_train)

    def decision_stump(X, y, alpha, threshold):
        scores = np.dot(X.T, y)
        sorted_scores = np.sort(scores)[::-1]
        return np.argmax(sorted_scores * alpha)
    clf_weights = [calculate_weight(y_train)]
    for _ in range(n_clf):
        y_pred = []
        for X_test_i in X_test:
            y_pred.append(decision_stump(X_train, y_train, clf_weights[-1], 1))
        aggregated_pred = np.mean(y_pred)
        for i in range(len(y_pred)):
            if aggregated_pred != np.sign(y_train[i]):
                clf_weights.append(calculate_weight(y_train))
                break
        else:
            continue
    return [int(aggregated_pred)] * X_test.shape[0]
