def elastic_net_regression(x_train, y_train, x_test, degree=1, reg_factor=0.05, l1_ratio=0.5, n_iterations=3000, learning_rate=0.01):
    if not isinstance(n_iterations, int):
        return -1
    if all([a >= 0 for a in [len(x_train[0]), len(y_train)]]):
        return -1
    X_train = []
    for i in range(len(x_train)):
        X_train.append(np.ones((len(x_train[i]), 1)) * x_train[i][i])
    X_train = np.array(X_train)
    w = np.zeros(degree + 1)
    for _ in range(n_iterations):
        for j in range(degree + 1):
            partial_wj = 0
            for i in range(len(x_train)):
                if i != j:
                    partial_wj += X_train[i][j] * y_train[i]
            partial_wj /= len(x_train)
            for k in range(degree + 1):
                if k == j:
                    continue
                partial_wk = reg_factor * w[k]
                if k < j:
                    partial_wk -= abs(partial_wj)
                if k > j:
                    partial_wk += abs(partial_wj)
                partial_wk *= l1_ratio
                w[k] -= learning_rate * partial_wk
    return np.round(w.tolist(), 4)
