def squared_log_error(actual, predicted):
    errors = [(np.log(1 + a) - np.log(1 + b)) ** 2 for (a, b) in zip(actual, predicted)]
    return np.mean(errors)
def mean_squared_log_error(actual, predicted):
    if len(actual) != len(predicted):
        return -1
    for i in range(len(actual)):
        if not all((a > 0 for a in [actual[i], predicted[i]])):
            return -1
    return squared_log_error(actual, predicted)
