@dataclass
class Leaf:
    value: object
def compare_trees(tree_a, tree_b):

    def check_equivalence(node_a, node_b):
        if isinstance(node_a, Leaf) and isinstance(node_b, Leaf):
            return node_a.value == node_b.value
        elif not isinstance(node_a, Leaf) or not isinstance(node_b, Leaf):
            return False
        if node_a.feature != node_b.feature:
            return False
        if node_a.threshold != node_b.threshold:
            return False
        if not check_equivalence(node_a.left, node_b.left) or not check_equivalence(node_a.right, node_b.right):
            return False
        return True
    if type(tree_a) != Leaf and type(tree_b) != Leaf:
        raise ValueError('Both trees must be of type Leaf')
    if tree_a.feature != tree_b.feature or tree_a.threshold != tree_b.threshold:
        return False
    return check_equivalence(tree_a, tree_b)
