def apriori_frequent_itemsets(transactions: list[list[int]], min_sup: float) -> list[tuple]:
    freq_items = {}
    for transaction in transactions:
        for item in set(transaction):
            if item not in freq_items:
                freq_items[item] = {'freq': 0}
            freq_items[item]['freq'] += 1
    supported_freq = [item for (item, _) in sorted(freq_items.items(), key=lambda x: (-x['freq'], x[0])) if x['freq'] >= min_sup]
    result = []
    seen = {tuple(sorted((v, k))): True for (k, v) in freq_items.items()}
    while supported_freq:
        next_supports = []
        for sup in supported_freq:
            candidate = frozenset(sup)
            if candidate not in seen or seen[candidate]:
                continue
            for prev_supp in supported_freq:
                if tuple(prev_supp - frozenset(sup)) in seen:
                    break
            else:
                next_supports.append(sup)
        result.extend(next_supports)
        seen.update({tuple(sorted((v, k))) for (k, v) in freq_items.items()})
    return result
