import numpy as np
def kmeans(X: np.ndarray, k: int, max_iters: int=100) -> tuple[list[int], list[list[float]]]:
    if not isinstance(k, int) or k < 1 or k > X.shape[1]:
        raise ValueError('k must be a positive integer between 1 and the number of dimensions in X')
    if not isinstance(max_iters, int) or max_iters <= 0:
        raise ValueError('max_iters must be a positive integer')
    if X.shape[0] != X.shape[1]:
        raise ValueError('X must be a square matrix')
    labels = []
    centroids = []
    for _ in range(max_iters):
        current_labels = [np.argmin(np.linalg.norm(X[:, i] - centroid, axis=1)) for (i, centroid) in enumerate(centroids)]
        unique_indices = set()
        for (index, value) in enumerate(current_labels):
            if value in unique_indices:
                continue
            else:
                unique_indices.add(value)
            labels.append(index)
            centroids.append(np.mean(X[:, unique_indices], axis=0))
        if len(set(labels)) == len(labels):
            break
        centroids = labels.copy()
    return (labels, centroids.tolist())
