from collections import Counter
def entropy(class_counts):
    """Calculate the entropy from class counts."""
    total = sum(class_counts)
    probs = [count / total for count in class_counts]
    return -sum((p * math.log2(p) for p in probs if p > 0))
def partition_by_attribute(examples, attribute):
    """Partition the examples by the given attribute."""
    partitions = {}
    for example in examples:
        value = example[attribute]
        if value not in partitions:
            partitions[value] = []
        partitions[value].append(example)
    return partitions
def majority_value(examples):
    """Return the most common target value in the examples."""
    class_counts = Counter((example['class'] for example in examples))
    return class_counts.most_common(1)[0][0]
def info_gain(examples, attribute, target_attr='class'):
    """Calculate the information gain for the given attribute."""
    parent_entropy = entropy(Counter((example[target_attr] for example in examples)).values())
    partitions = partition_by_attribute(examples, attribute)
    weighted_entropy = sum((len(partition) / len(examples) * entropy(Counter((example[target_attr] for example in partition)).values()) for partition in partitions.values()))
    return parent_entropy - weighted_entropy
def choose_best_attribute(examples, attributes, target_attr='class'):
    """Choose the best attribute to split on based on information gain."""
    return max(attributes, key=lambda attr: info_gain(examples, attr, target_attr))
def create_decision_tree(examples, attributes, target_attr='class'):
    """Recursively create a decision tree."""
    class_counts = Counter((example[target_attr] for example in examples))
    if len(class_counts) == 1:
        return next(iter(class_counts))
    if not attributes:
        return majority_value(examples)
    best_attr = choose_best_attribute(examples, attributes, target_attr)
    decision_tree = {best_attr: {}}
    attributes.remove(best_attr)
    partitions = partition_by_attribute(examples, best_attr)
    for (value, partition) in partitions.items():
        sub_tree = create_decision_tree(partition, attributes.copy(), target_attr)
        decision_tree[best_attr][value] = sub_tree
    return decision_tree
def learn_decision_tree(examples: list[dict], attributes: list[str], target_attr: str) -> dict:
    return create_decision_tree(examples, attributes, target_attr)
