class Dense(Layer):

    def __init__(self, n_units, input_shape=None):
        self.layer_input = None
        self.input_shape = input_shape
        self.n_units = n_units
        self.trainable = True
        self.W = None
        self.w0 = None
        self.W_optim = None
        self.w0_optim = None
        if input_shape is not None:
            self.initialize()

    def initialize(self, optimizer='SGD', init_type=' XavierUniform '):
        init_func = self.__get_initialization(init_type)
        limit = 1 / math.sqrt(self.input_shape[0])
        self.W = init_func(self.n_units, self.input_shape[0], limit)
        self.w0 = np.zeros((self.n_units, 1))
        if self.W_optim is None:
            self.W_optim = copy.copy(optimizer)
            self.w0_optim = copy.copy(optimizer)

    def __get_initialization(self, init_type):
        if ' Xavier ' in init_type:
            if ' Uniform ' in init_type:

                def initializer(out_size, in_size, limit):
                    return np.random.uniform(low=-limit, high=limit, size=(out_size, in_size))
                return initializer
            elif ' Normal ' in init_type:

                def initializer(out_size, in_size, limit):
                    std_dev = limit * math.sqrt(3)
                    return np.random.normal(loc=0.0, scale=std_dev, size=(out_size, in_size))
                return initializer
        else:
            return self.__get_initialization(' XavierUniform ')

    def parameters(self):
        return np.prod(self.W.shape) + np.prod(self.w0.shape)

    def forward_pass(self, X, training=True):
        self.layer_input = X
        return np.dot(self.W, self.layer_input) + self.w0

    def backward_pass(self, accum_grad):
        W = self.W
        if self.trainable:
            self.W_optim.update(self.W, accum_grad.dot(self.layer_input.T).reshape(*self.W.shape))
            self.w0_optim.update(self.w0, accum_grad.sum(axis=1, keepdims=True))
        return W.T.dot(accum_grad)

    def output_shape(self):
        return (self.n_units,)
