import numpy as np
def create_row_hv(row, dim, random_seeds):
    """
    Creates a composite hypervector for a given dataset row using Hyperdimensional Computing (HDC).

    Parameters:
    - row: A dictionary representing a dataset row, where keys are feature names and values are their corresponding values.
    - dim: The dimensionality of the hypervectors.
    - random_seeds: A dictionary where keys are feature names and values are seeds to ensure reproducibility of hypervectors.

    Returns:
    - A composite hypervector representing the entire row as a list.
    """

    def initialize_hdvector(dimension, seed):
        """Initialize a hypervector with the given dimension and seed."""
        np.random.seed(seed)
        return (np.random.rand(dimension) * 2 - 1).astype(np.float32)

    def bind(hv1, hv2):
        """Bind two hypervectors."""
        return np.sign(hv1 * hv2).astype(np.float32)
    feature_vectors = {}
    for feature in row.keys():
        if feature not in feature_vectors:
            feature_vectors[feature] = initialize_hdvector(dim, random_seeds[feature])
    composite_hv = None
    for (feature, value) in row.items():
        value_seed = hash(str(value) + feature) % 2 ** 32
        value_vector = initialize_hdvector(dim, value_seed)
        feature_value_hv = bind(feature_vectors[feature], value_vector)
        if composite_hv is None:
            composite_hv = feature_value_hv
        else:
            composite_hv = bind(composite_hv, feature_value_hv)
    return composite_hv.tolist()
