import numpy as np
class MultiHeadAttention:

    def __init__(self, n_head, n_embd):
        self.n_head = n_head
        self.head_size = n_embd // n_head
        self.wq = np.random.randn(n_embd, n_embd)
        self.wk = np.random.randn(n_embd, n_embd)
        self.wv = np.random.randn(n_embd, n_embd)
        self.wo = np.random.randn(n_embd, n_embd)

    def forward(self, x):
        (B, T, C) = x.shape
        k = x.dot(self.wk)
        q = x.dot(self.wq)
        v = x.dot(self.wv)
        k = k.reshape(B, T, self.n_head, self.head_size).transpose(0, 2, 1, 3)
        q = q.reshape(B, T, self.n_head, self.head_size).transpose(0, 2, 1, 3)
        v = v.reshape(B, T, self.n_head, self.head_size).transpose(0, 2, 1, 3)
        wei = q @ k.transpose(-2, -1) * self.head_size ** (-0.5)
        wei = np_softmax(wei, axis=-1)
        out = wei @ v
        out = out.transpose(0, 2, 1, 3).reshape(B, T, C)
        return out.dot(self.wo)
def np_softmax(x, axis=None):
    return np.exp(x - np.max(x, axis=axis, keepdims=True)) / np.sum(np.exp(x - np.max(x, axis=axis, keepdims=True)), axis=axis, keepdims=True)
class MLP:

    def __init__(self, n_embd, n_hidden):
        self.c_fc = np.random.randn(n_embd, n_hidden)
        self.c_proj = np.random.randn(n_hidden, n_embd)

    def forward(self, x):
        return x.dot(self.c_fc).dot(self.c_proj)
class LayerNorm:

    def __init__(self, features_shape, eps=1e-06):
        self.eps = eps
        self.g = np.ones(features_shape)
        self.b = np.zeros(features_shape)

    def forward(self, x):
        mean = np.mean(x, axis=-1, keepdims=True)
        var = np.var(x, axis=-1, keepdims=True)
        return self.g * (x - mean) / np.sqrt(var + self.eps) + self.b
class Block:

    def __init__(self, n_embd, n_head):
        self.ln_1 = LayerNorm(features_shape=n_embd)
        self.attn = MultiHeadAttention(n_head=n_head, n_embd=n_embd)
        self.ln_2 = LayerNorm(features_shape=n_embd)
        self.mlp = MLP(n_embd=n_embd, n_hidden=4 * n_embd)

    def forward(self, x):
        x = x + self.attn.forward(self.ln_1.forward(x))
        x = x + self.mlp.forward(self.ln_2.forward(x))
        return x
class GPTModel:

    def __init__(self, params):
        self.wte = params['wte']
        self.wpe = params['wpe']
        self.blocks = [Block(n_embd=self.wte.shape[1], n_head=params['hparams']['n_head']) for _ in params['blocks']]
        self.ln_f = LayerNorm(features_shape=self.wte.shape[1])
        self.ln_f.g = params['ln_f']['g']
        self.ln_f.b = params['ln_f']['b']

    def forward(self, idx, targets=None):
        (B, T) = idx.shape
        tok_emb = self.wte[idx]
        pos_emb = self.wpe[:T]
        x = tok_emb + pos_emb
        for block in self.blocks:
            x = block.forward(x)
        x = self.ln_f.forward(x)
        logits = x @ self.wte.T
        return logits
